import React, { useState, useEffect } from 'react';
import SimpleVoice from '../components/SimpleVoice';
import audioService from '../services/audioService';

const VoiceTest = () => {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [text, setText] = useState('Este es un mensaje de prueba para la síntesis de voz');
  const [recognizedText, setRecognizedText] = useState('');
  const [status, setStatus] = useState('');
  const [voicesAvailable, setVoicesAvailable] = useState([]);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [recognitionSupported, setRecognitionSupported] = useState(false);

  useEffect(() => {
    // Inicializar el sistema de voz y comprobar compatibilidad
    const speechSystemInitialized = audioService.initSpeechSystem();
    const recognitionSupported = audioService.isSupportedByBrowser();

    setSpeechSupported(speechSystemInitialized);
    setRecognitionSupported(recognitionSupported);

    // Obtener lista de voces disponibles
    setTimeout(() => {
      if (window.speechSynthesis) {
        const voices = window.speechSynthesis.getVoices();
        const spanishVoices = voices.filter(v => 
          v.lang.includes('es') || 
          v.name.toLowerCase().includes('spanish')
        );
        setVoicesAvailable(spanishVoices.length > 0 ? spanishVoices : voices);
      }
    }, 500);

    return () => {
      // Limpiar al desmontar
      if (window.speechSynthesis) {
        window.speechSynthesis.cancel();
      }
      if (isListening) {
        audioService.stop();
      }
    };
  }, []);

  // Función para iniciar la síntesis de voz
  const handleSpeak = () => {
    if (!text.trim()) {
      setStatus('El texto no puede estar vacío');
      return;
    }

    setIsSpeaking(true);
    setStatus('Hablando...');

    audioService.speakText(
      text,
      () => {
        setStatus('Síntesis iniciada');
      },
      () => {
        setIsSpeaking(false);
        setStatus('Síntesis finalizada');
      },
      (error) => {
        setIsSpeaking(false);
        setStatus(`Error: ${error}`);
      }
    );
  };

  // Función para detener la síntesis de voz
  const handleStop = () => {
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
      setIsSpeaking(false);
      setStatus('Síntesis detenida');
    }
  };

  // Iniciar reconocimiento de voz
  const startVoiceRecognition = () => {
    if (!audioService.isSupportedByBrowser()) {
      setStatus('Tu navegador no soporta reconocimiento de voz');
      return;
    }

    audioService.init('es-ES');

    audioService.onResult((transcript, isFinal) => {
      setRecognizedText(transcript);
      setStatus(isFinal ? 'Reconocimiento finalizado' : 'Escuchando...');
    });

    audioService.onEnd((finalTranscript) => {
      setIsListening(false);
      setStatus('Reconocimiento finalizado');
    });

    audioService.onError((errorMessage) => {
      setStatus(`Error de reconocimiento: ${errorMessage}`);
      setIsListening(false);
    });

    const started = audioService.start();
    if (started) {
      setIsListening(true);
      setStatus('Escuchando...');
    } else {
      setStatus('No se pudo iniciar el reconocimiento de voz');
    }
  };

  // Detener reconocimiento de voz
  const stopVoiceRecognition = () => {
    audioService.stop();
    setIsListening(false);
    setStatus('Reconocimiento detenido');
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-center">Prueba de Voz</h1>

      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <h2 className="text-xl font-bold mb-4">Estado del Sistema</h2>
        <ul className="list-disc pl-5 space-y-2">
          <li>
            Síntesis de voz: 
            <span className={`ml-2 font-semibold ${speechSupported ? 'text-green-600' : 'text-red-600'}`}>
              {speechSupported ? 'Soportada' : 'No soportada'}
            </span>
          </li>
          <li>
            Reconocimiento de voz: 
            <span className={`ml-2 font-semibold ${recognitionSupported ? 'text-green-600' : 'text-red-600'}`}>
              {recognitionSupported ? 'Soportado' : 'No soportado'}
            </span>
          </li>
          <li>
            Voces disponibles: {voicesAvailable.length}
            {voicesAvailable.length > 0 && (
              <ul className="list-disc pl-5 text-sm text-gray-600">
                {voicesAvailable.slice(0, 5).map((voice, index) => (
                  <li key={index}>{voice.name} ({voice.lang})</li>
                ))}
                {voicesAvailable.length > 5 && <li>...</li>}
              </ul>
            )}
          </li>
        </ul>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Sección de síntesis de voz */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-bold mb-4">Prueba de Síntesis</h2>

          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Texto para sintetizar:</label>
            <textarea
              value={text}
              onChange={(e) => setText(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              rows="4"
            ></textarea>
          </div>

          <div className="flex space-x-2">
            <button
              onClick={handleSpeak}
              disabled={isSpeaking}
              className={`px-4 py-2 rounded-md ${
                isSpeaking ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'
              }`}
            >
              Hablar
            </button>
            <button
              onClick={handleStop}
              disabled={!isSpeaking}
              className={`px-4 py-2 rounded-md ${
                !isSpeaking ? 'bg-gray-400 cursor-not-allowed' : 'bg-red-600 hover:bg-red-700 text-white'
              }`}
            >
              Detener
            </button>
          </div>

          {isSpeaking && <SimpleVoice text="" />}
        </div>

        {/* Sección de reconocimiento de voz */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-bold mb-4">Prueba de Reconocimiento</h2>

          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Texto reconocido:</label>
            <div className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 min-h-[6rem]">
              {recognizedText || "No se ha reconocido texto aún..."}
            </div>
          </div>

          <div className="flex space-x-2">
            <button
              onClick={startVoiceRecognition}
              disabled={isListening}
              className={`px-4 py-2 rounded-md ${
                isListening ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700 text-white'
              }`}
            >
              Iniciar Reconocimiento
            </button>
            <button
              onClick={stopVoiceRecognition}
              disabled={!isListening}
              className={`px-4 py-2 rounded-md ${
                !isListening ? 'bg-gray-400 cursor-not-allowed' : 'bg-red-600 hover:bg-red-700 text-white'
              }`}
            >
              Detener
            </button>
          </div>
        </div>
      </div>

      {/* Estado de las operaciones */}
      <div className="mt-6 p-4 rounded-md bg-blue-50 text-center">
        <p className="font-semibold">{status || "Listo para pruebas"}</p>
      </div>
    </div>
  );
};

export default VoiceTest;
