import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  isSpeechRecognitionSupported,
  isSpeechSynthesisSupported,
  listenForSpeech,
  speakText,
  processVoiceResponse,
  checkMicrophonePermission
} from '../utils/voiceSurveyAPI';
import { SurveyService, ResponseService } from '../services/apiService';
import SimpleVoice from '../components/SimpleVoice';

const TakeSurvey = () => {
  const { surveyId } = useParams();
  const navigate = useNavigate();
  const [survey, setSurvey] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentStep, setCurrentStep] = useState('welcome'); // welcome, name, questions, thanks
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userName, setUserName] = useState('');
  const [responses, setResponses] = useState([]);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [browserSupported, setBrowserSupported] = useState(true);
  const [microphonePermission, setMicrophonePermission] = useState(null);
  const [showThankYouMessage, setShowThankYouMessage] = useState(false);

  // Frases para mejorar la naturalidad de la conversación
  const CONVERSATION_PHRASES = {
    acknowledgments: [
      'Entendido.',
      'Perfecto.',
      'Muy bien.',
      'Gracias por su respuesta.',
      'He registrado su respuesta.'
    ],
    clarifications: [
      'No he entendido bien. ¿Podría repetirlo?',
      'Disculpe, ¿podría decirlo de nuevo?',
      'No pude entender su respuesta. Intentemos de nuevo.'
    ],
    transitions: [
      'Pasemos a la siguiente pregunta.',
      'Continuamos con la siguiente pregunta.',
      'Ahora, la siguiente pregunta.'
    ]
  };

  // Comprobar compatibilidad del navegador y permisos
  useEffect(() => {
    const checkCompatibility = async () => {
      // Verificar si el navegador soporta reconocimiento de voz
      const speechRecognitionSupported = isSpeechRecognitionSupported();
      const speechSynthesisSupported = isSpeechSynthesisSupported();

      if (!speechRecognitionSupported || !speechSynthesisSupported) {
        setBrowserSupported(false);
        setErrorMessage('Su navegador no soporta las tecnologías de voz necesarias para esta encuesta. Por favor, use Chrome, Edge o Safari.');
        return;
      }

      // Verificar permisos de micrófono
      const hasMicrophonePermission = await checkMicrophonePermission();
      setMicrophonePermission(hasMicrophonePermission);

      if (!hasMicrophonePermission) {
        setErrorMessage('Necesitamos permiso para usar su micrófono. Por favor, permita el acceso cuando se le solicite.');
      }
    };

    checkCompatibility();
  }, []);

  // Obtener datos de la encuesta
  useEffect(() => {
    const fetchSurvey = async () => {
      try {
        setLoading(true);
        console.log(`Intentando cargar la encuesta con ID: ${surveyId}`);

        // Usar el servicio centralizado para obtener la encuesta pública
        const data = await SurveyService.getPublicSurvey(surveyId);

        if (data) {
          console.log('Encuesta cargada exitosamente:', data);
          setSurvey(data);
          // Inicializar array de respuestas
          setResponses(data.questions.map(() => ({ value: '', timestamp: null })));
        } else {
          console.error('La respuesta no contiene datos');
          setErrorMessage('La encuesta no contiene datos válidos.');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching survey:', error);

        // Mensajes de error más específicos
        if (error.response) {
          // El servidor respondió con un código de error
          if (error.response.status === 404) {
            setErrorMessage('Encuesta no encontrada. El ID proporcionado no corresponde a ninguna encuesta existente.');
          } else if (error.response.status === 403) {
            setErrorMessage('Esta encuesta ya no está activa o no es pública.');
          } else {
            setErrorMessage(`Error del servidor: ${error.response.data?.message || 'Error desconocido'}`);
          }
        } else if (error.request) {
          // La solicitud se hizo pero no se recibió respuesta
          setErrorMessage('No se pudo conectar con el servidor. Verifica tu conexión a internet.');
        } else {
          // Error al configurar la solicitud
          setErrorMessage('Error al crear la solicitud: ' + error.message);
        }

        setLoading(false);
      }
    };

    if (surveyId) {
      fetchSurvey();
    }
  }, [surveyId]);

  // Efecto para iniciar la conversación cuando la encuesta se carga correctamente
  useEffect(() => {
    if (survey && browserSupported && microphonePermission && !loading) {
      // Pequeño retraso para asegurar que todo está listo
      setTimeout(() => {
        startConversation();
      }, 1000);
    }
  }, [survey, browserSupported, microphonePermission, loading]);

  // Iniciar la conversación automáticamente
  const startConversation = async () => {
    const welcomeMessage = `Bienvenido a la encuesta de voz: ${survey.title}. ${survey.description || ''}`;
    await speakText(welcomeMessage);

    if (survey.requiresName) {
      setCurrentStep('name');
      await speakText('Para empezar, ¿podría decirme su nombre?');
      startListening();
    } else {
      moveToQuestions();
    }
  };

  // Mover a la sección de preguntas
  const moveToQuestions = async () => {
    setCurrentStep('questions');
    setCurrentQuestionIndex(0);
    await speakCurrentQuestion();
  };

  // Hablar la pregunta actual
  const speakCurrentQuestion = async () => {
    if (!survey || currentQuestionIndex >= survey.questions.length) return;

    const question = survey.questions[currentQuestionIndex];
    let questionText = question.text;

    // Añadir instrucciones específicas según tipo de pregunta
    if (question.type === 'rating') {
      questionText += ' Por favor, responda con un número del 1 al 5, donde 1 es lo más bajo y 5 lo más alto.';
    } else if (question.type === 'yesno') {
      questionText += ' Por favor, responda sí o no.';
    }

    // Hablar la pregunta
    setIsSpeaking(true);
    await speakText(questionText);
    setIsSpeaking(false);

    // Iniciar escucha automáticamente
    startListening();
  };

  // Función para cuando termina de hablar la pregunta
  const onQuestionSpeakEnd = () => {
    setTimeout(startListening, 500);
  };

  // Función para cuando termina de hablar el mensaje de agradecimiento
  const onThankYouSpeakEnd = async () => {
    try {
      // Enviar respuestas al servidor
      await submitResponses();
    } catch (error) {
      console.error('Error submitting responses:', error);
    }
  };

  // Función para enviar respuestas al servidor
  const submitResponses = async () => {
    try {
      const formattedResponses = responses.map((response, index) => ({
        questionId: survey.questions[index]._id,
        questionText: survey.questions[index].text,
        answer: response.value,
        timestamp: response.timestamp
      }));

      // Usar el servicio centralizado para enviar respuestas
      await ResponseService.submitResponse({
        surveyId: survey._id,
        surveyTitle: survey.title,
        respondentName: userName || 'Anónimo',
        responses: formattedResponses
      });

      console.log('Respuestas enviadas correctamente');
      setCurrentStep('thanks');

      // Hablar mensaje de agradecimiento
      const thankYouMessage = survey.farewell || 'Gracias por completar esta encuesta. Sus respuestas han sido registradas correctamente.';
      await speakText(thankYouMessage);
      setShowThankYouMessage(true);

    } catch (error) {
      console.error('Error al enviar respuestas:', error);
      setErrorMessage('Error al enviar respuestas. Por favor, intente nuevamente.');
    }
  };

  // Función para iniciar escucha automáticamente
  const startListening = async () => {
    if (isListening) return;

    setIsListening(true);
    setTranscript('');

    try {
      const speechResult = await listenForSpeech();
      setIsListening(false);
      processResponse(speechResult);
    } catch (error) {
      console.error('Error en reconocimiento de voz:', error);
      setIsListening(false);

      // Si hubo un error, intentar nuevamente
      await speakText(CONVERSATION_PHRASES.clarifications[Math.floor(Math.random() * CONVERSATION_PHRASES.clarifications.length)]);
      setTimeout(() => startListening(), 1000);
    }
  };

  // Función para procesar la respuesta del usuario con NLP mejorado
  const processResponse = async (text) => {
    setTranscript(text);

    if (currentStep === 'name') {
      // Extraer el nombre usando técnicas básicas de NLP
      const possibleName = extractName(text);
      setUserName(possibleName);

      // Confirmar y seguir
      await speakText(`Gracias, ${possibleName}. Comencemos con la encuesta.`);
      moveToQuestions();
      return;
    }

    if (currentStep === 'questions') {
      const currentQuestion = survey.questions[currentQuestionIndex];

      // Procesar la respuesta según el tipo de pregunta
      const processedResponse = processVoiceResponse(text, currentQuestion.type);

      if (processedResponse !== null) {
        // Respuesta válida
        saveResponse(processedResponse);

        // Dar feedback al usuario
        const acknowledgment = CONVERSATION_PHRASES.acknowledgments[Math.floor(Math.random() * CONVERSATION_PHRASES.acknowledgments.length)];
        await speakText(acknowledgment);

        // Transición a siguiente pregunta o finalizar
        if (currentQuestionIndex < survey.questions.length - 1) {
          const transition = CONVERSATION_PHRASES.transitions[Math.floor(Math.random() * CONVERSATION_PHRASES.transitions.length)];
          await speakText(transition);
          setCurrentQuestionIndex(prevIndex => prevIndex + 1);
          setTimeout(() => speakCurrentQuestion(), 500);
        } else {
          // Finalizar encuesta
          submitResponses();
        }
      } else {
        // Respuesta inválida, pedir aclaración
        const clarification = CONVERSATION_PHRASES.clarifications[Math.floor(Math.random() * CONVERSATION_PHRASES.clarifications.length)];
        await speakText(clarification);
        startListening();
      }
    }
  };

  // Extraer un posible nombre de un texto
  const extractName = (text) => {
    // Eliminar frases comunes de presentación
    const cleanText = text.replace(/mi nombre es|me llamo|soy/gi, '').trim();

    // Si solo queda una palabra, probablemente es el nombre
    const words = cleanText.split(' ');
    if (words.length === 1) return words[0];

    // Si hay múltiples palabras, tomamos las primeras 2 (nombre y posible apellido)
    return words.slice(0, 2).join(' ');
  };

  // Función para guardar respuesta y pasar a la siguiente pregunta
  const saveResponse = (value) => {
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = {
      value,
      timestamp: new Date().toISOString()
    };
    setResponses(updatedResponses);

    // Verificar si hay más preguntas
    if (currentQuestionIndex < survey.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Fin de la encuesta
      setCurrentStep('thanks');
    }
  };

  // Manejar clic en botón de micrófono
  const handleMicrophoneClick = () => {
    if (isListening) {
      setIsListening(false);
    } else {
      startListening();
    }
  };

  // Manejar clic en botones de opciones 
  const handleOptionClick = (value) => {
    if (isListening) return;

    processResponse(value);
  };

  // Solicitar permisos de micrófono
  const requestMicrophonePermission = async () => {
    try {
      const permission = await checkMicrophonePermission();
      setMicrophonePermission(permission);

      if (permission) {
        // Iniciar la encuesta si se conceden los permisos
        setCurrentStep('welcome');
      } else {
        setErrorMessage('No se pudo obtener permiso para el micrófono. Por favor, permita el acceso en la configuración de su navegador.');
      }
    } catch (error) {
      console.error('Error al solicitar permisos de micrófono:', error);
      setErrorMessage('Error al solicitar permisos de micrófono.');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center my-12">
        <svg className="animate-spin h-8 w-8 text-primary-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
      </div>
    );
  }

  if (!survey) {
    return (
      <div className="card text-center py-12">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
        </svg>
        <h3 className="text-lg font-medium text-gray-800 mb-2">Encuesta no encontrada</h3>
        <p className="text-gray-600 mb-6">La encuesta que estás buscando no existe o ha sido eliminada.</p>
        <button onClick={() => navigate('/')} className="btn btn-primary">
          Volver al Inicio
        </button>
      </div>
    );
  }

  if (!browserSupported) {
    return (
      <div className="card text-center py-12">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto text-orange-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
        </svg>
        <h3 className="text-lg font-medium text-gray-800 mb-2">Navegador no compatible</h3>
        <p className="text-gray-600 mb-6">
          Esta aplicación requiere un navegador compatible con la API de Reconocimiento de Voz.
          Por favor, utilice Chrome, Edge o Safari para continuar.
        </p>
        <button onClick={() => window.location.reload()} className="btn btn-primary">
          Intentar Nuevamente
        </button>
      </div>
    );
  }

  if (microphonePermission === false) {
    return (
      <div className="card text-center py-12">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto text-orange-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 10a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1v-4a1 1 0 01-1-1h-2a1 1 0 01-1-1v-2a1 1 0 01 1-1h2a1 1 0 01 1 1v2a1 1 0 01 1 1h2a1 1 0 01 1 1v2a1 1 0 01-1 1h-2a1 1 0 01-1-1v-2a1 1 0 011-1h2z" />
        </svg>
        <h3 className="text-lg font-medium text-gray-800 mb-2">Permiso de Micrófono Requerido</h3>
        <p className="text-gray-600 mb-6">
          Para participar en esta encuesta por voz, necesitamos permiso para usar su micrófono.
          Por favor, haga clic en el botón debajo para conceder acceso.
        </p>
        <button onClick={requestMicrophonePermission} className="btn btn-primary">
          Permitir Micrófono
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto p-4">
      <div className="bg-white shadow-md rounded-lg p-6 mb-6">
        {/* Encabezado */}
        <h1 className="text-2xl font-bold mb-4">{loading ? 'Cargando encuesta...' : (survey ? survey.title : 'Encuesta no encontrada')}</h1>

        {/* Mensajes de error */}
        {errorMessage && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            <p>{errorMessage}</p>
            {!microphonePermission && (
              <button
                onClick={requestMicrophonePermission}
                className="mt-2 btn btn-primary"
              >
                Permitir acceso al micrófono
              </button>
            )}
          </div>
        )}

        {/* Contenido principal */}
        <div className="mb-6">
          {loading ? (
            <div className="flex justify-center items-center h-40">
              <div className="spinner"></div>
            </div>
          ) : !browserSupported ? (
            <div className="bg-yellow-100 border border-yellow-400 text-yellow-700 px-4 py-3 rounded">
              <p>Su navegador no soporta las tecnologías de voz necesarias para esta encuesta.</p>
              <p className="mt-2">Por favor, use un navegador compatible como Chrome, Edge o Safari.</p>
            </div>
          ) : (
            <div>
              {/* Indicador de escucha activa */}
              {isListening && (
                <div className="fixed bottom-4 right-4 bg-primary-500 text-white py-2 px-4 rounded-full shadow-lg animate-pulse flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
                  </svg>
                  Escuchando...
                </div>
              )}

              {/* Indicador de habla activa */}
              {isSpeaking && (
                <div className="fixed bottom-4 left-4 bg-blue-500 text-white py-2 px-4 rounded-full shadow-lg animate-pulse flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.536 8.464a5 5 0 010 7.072m2.828-9.9a9 9 0 010 12.728M5.586 15.536a5 5 0 017.072 0m-9.9-2.828a9 9 0 0112.728 0" />
                  </svg>
                  Hablando...
                </div>
              )}

              {/* Transcripción de voz */}
              {transcript && (
                <div className="bg-gray-100 p-4 rounded-lg mb-6">
                  <div className="text-sm text-gray-500 mb-1">Su respuesta:</div>
                  <div className="text-lg">{transcript}</div>
                </div>
              )}

              {/* Contenido según el paso actual */}
              {currentStep === 'questions' && currentQuestionIndex < survey?.questions?.length && (
                <div>
                  <div className="mb-4">
                    <h3 className="text-lg font-semibold mb-2">
                      Pregunta {currentQuestionIndex + 1} de {survey.questions.length}
                    </h3>
                    <p className="text-lg">{survey.questions[currentQuestionIndex].text}</p>
                  </div>
                </div>
              )}

              {/* Progreso de la encuesta */}
              {currentStep === 'questions' && (
                <div className="mt-8">
                  <div className="w-full bg-gray-200 rounded-full h-2.5">
                    <div
                      className="bg-primary-500 h-2.5 rounded-full"
                      style={{ width: `${((currentQuestionIndex) / survey.questions.length) * 100}%` }}
                    ></div>
                  </div>
                  <div className="text-xs text-gray-500 mt-2">
                    {currentQuestionIndex} de {survey.questions.length} preguntas completadas
                  </div>
                </div>
              )}

              {/* Mensaje final */}
              {currentStep === 'thanks' && (
                <div className="mt-6 space-y-4">
                  <p className="text-green-600">Sus respuestas han sido guardadas correctamente.</p>
                  <p>{survey.farewell || 'Gracias por completar esta encuesta.'}</p>

                  {showThankYouMessage && (
                    <div className="mt-6 flex space-x-4 justify-center">
                      <button
                        onClick={() => navigate('/surveys/public')}
                        className="btn btn-primary"
                      >
                        Ver Otras Encuestas
                      </button>
                      <button
                        onClick={() => navigate('/')}
                        className="btn btn-outline"
                      >
                        Volver al Inicio
                      </button>
                    </div>
                  )}
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Instrucciones actualizadas */}
      <div className="bg-gray-50 p-4 rounded-lg text-sm text-gray-600">
        <h3 className="font-medium mb-2">Instrucciones:</h3>
        <ul className="list-disc list-inside space-y-1">
          <li>Esta encuesta se realiza completamente por voz, de forma automática</li>
          <li>Escuche atentamente cada pregunta y responda cuando termine de escucharla</li>
          <li>Para preguntas de calificación, responda con un número del 1 al 5</li>
          <li>Para preguntas de sí/no, responda con "sí" o "no"</li>
          <li>Hable con claridad para que el sistema pueda entenderle correctamente</li>
        </ul>
      </div>
    </div>
  );
};

export default TakeSurvey;
