import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { SurveyService, ResponseService } from '../services/apiService';

// Componentes
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';
import audioService from '../services/audioService';

const TakeSurvey = () => {
  const { surveyId } = useParams();
  const navigate = useNavigate();
  
  // Referencias para controlar el flujo de la conversaci√≥n
  const conversationActive = useRef(false);
  const speakTimeoutRef = useRef(null);
  
  // Estados
  const [survey, setSurvey] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [currentResponse, setCurrentResponse] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [conversationState, setConversationState] = useState('idle'); // idle, speaking, listening, processing
  const [conversationMessage, setConversationMessage] = useState('');
  
  // Cargar la encuesta
  useEffect(() => {
    const fetchSurvey = async () => {
      try {
        const data = await SurveyService.getPublicSurvey(surveyId);
        console.log('Encuesta cargada:', data);
        setSurvey(data);
        // Inicializar respuestas vac√≠as
        setResponses(new Array(data.questions.length).fill(''));
      } catch (error) {
        console.error('Error al cargar la encuesta:', error);
        setError('No se pudo cargar la encuesta. Verifique que el ID sea correcto y que la encuesta est√© activa.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchSurvey();
    
    // Inicializar sistema de voz
    audioService.initSpeechSystem();
    
    return () => {
      // Detener cualquier s√≠ntesis y reconocimiento al desmontar
      if (speakTimeoutRef.current) {
        clearTimeout(speakTimeoutRef.current);
      }
      audioService.stop();
      conversationActive.current = false;
      
      if (window.speechSynthesis) {
        window.speechSynthesis.cancel();
      }
    };
  }, [surveyId]);
  
  // Iniciar la conversaci√≥n una vez que la encuesta est√© cargada
  useEffect(() => {
    if (survey && voiceEnabled && !conversationActive.current) {
      startConversation();
    }
  }, [survey, voiceEnabled]);
  
  // Funci√≥n para iniciar la conversaci√≥n
  const startConversation = () => {
    if (!voiceEnabled || !survey) return;
    
    conversationActive.current = true;
    
    // Dar un mensaje de bienvenida
    if (survey.welcomeMessage) {
      speakText(survey.welcomeMessage, () => {
        // Despu√©s de la bienvenida, preguntar la primera pregunta
        speakCurrentQuestion();
      });
    } else {
      // Si no hay mensaje de bienvenida, ir directamente a la primera pregunta
      speakCurrentQuestion();
    }
  };
  
  // Funci√≥n para hablar el texto usando el servicio de audio
  const speakText = (text, onEndCallback) => {
    if (!voiceEnabled) {
      if (onEndCallback) onEndCallback();
      return;
    }
    
    setConversationState('speaking');
    setConversationMessage('Hablando...');
    
    audioService.speakText(
      text,
      () => {
        console.log('Hablando:', text);
      },
      () => {
        console.log('Termin√≥ de hablar');
        setConversationState('idle');
        if (onEndCallback) {
          // Peque√±o retraso para una experiencia m√°s natural
          speakTimeoutRef.current = setTimeout(onEndCallback, 500);
        }
      },
      (error) => {
        console.error('Error al hablar:', error);
        setConversationState('idle');
        if (onEndCallback) onEndCallback();
      }
    );
  };
  
  // Funci√≥n para hablar la pregunta actual
  const speakCurrentQuestion = () => {
    if (!survey || !voiceEnabled) return;
    
    const currentQuestion = survey.questions[currentQuestionIndex];
    if (!currentQuestion) return;
    
    let questionText = `Pregunta ${currentQuestionIndex + 1}: ${currentQuestion.text}`;
    
    // Agregar informaci√≥n sobre opciones si es pregunta de opci√≥n m√∫ltiple
    if (currentQuestion.type === 'multiple_choice' && currentQuestion.options) {
      questionText += '. Las opciones son: ';
      questionText += currentQuestion.options.map((option, idx) => 
        `Opci√≥n ${idx + 1}: ${option}`
      ).join(', ');
    }
    
    speakText(questionText, () => {
      // Despu√©s de hablar la pregunta, iniciar el reconocimiento de voz
      startListening();
    });
  };
  
  // Funci√≥n para iniciar la escucha de la respuesta del usuario
  const startListening = () => {
    if (!voiceEnabled) return;
    
    if (!audioService.isSupportedByBrowser()) {
      setError('Tu navegador no soporta reconocimiento de voz');
      return;
    }
    
    audioService.init('es-ES');
    
    audioService.onResult((transcript, isFinal) => {
      setCurrentResponse(transcript);
      if (isFinal) {
        setConversationState('processing');
        setConversationMessage('Procesando respuesta...');
      }
    });
    
    audioService.onEnd((finalTranscript) => {
      setIsListening(false);
      setConversationState('processing');
      setConversationMessage('Procesando respuesta...');
      
      // Guardar la respuesta y continuar la conversaci√≥n
      handleVoiceResponse(finalTranscript);
    });
    
    audioService.onError((errorMessage) => {
      console.error('Error de reconocimiento:', errorMessage);
      setError(`Error de reconocimiento: ${errorMessage}`);
      setIsListening(false);
      setConversationState('idle');
      
      // Reintentar la escucha despu√©s de un error
      speakTimeoutRef.current = setTimeout(() => {
        speakText('No pude entender tu respuesta. Por favor, int√©ntalo de nuevo.', () => {
          startListening();
        });
      }, 1000);
    });
    
    const started = audioService.start();
    if (started) {
      setIsListening(true);
      setConversationState('listening');
      setConversationMessage('Escuchando...');
    } else {
      setError('No se pudo iniciar el reconocimiento de voz');
      setConversationState('idle');
    }
  };
  
  // Manejar la respuesta por voz
  const handleVoiceResponse = (response) => {
    if (!response || response.trim() === '') {
      // Si no hay respuesta, pedir que repita
      speakText('No he escuchado ninguna respuesta. Por favor, intenta de nuevo.', () => {
        startListening();
      });
      return;
    }
    
    // Guardar la respuesta actual
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = response;
    setResponses(updatedResponses);
    
    // Dar feedback al usuario
    speakText(`He registrado tu respuesta: ${response}.`, () => {
      // Pasar a la siguiente pregunta o finalizar
      if (currentQuestionIndex < survey.questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setCurrentResponse('');
        // Hablar la siguiente pregunta despu√©s de un breve pausa
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 1000);
      } else {
        // Si es la √∫ltima pregunta, mostrar confirmaci√≥n
        setShowConfirmation(true);
        speakText('Hemos terminado todas las preguntas. Ahora te mostrar√© un resumen de tus respuestas para que las confirmes.', () => {
          speakConfirmationSummary();
        });
      }
    });
  };
  
  // Leer el resumen de confirmaci√≥n
  const speakConfirmationSummary = () => {
    if (!voiceEnabled || !showConfirmation) return;
    
    let summaryText = 'Resumen de tus respuestas: ';
    
    survey.questions.forEach((question, index) => {
      summaryText += `Pregunta ${index + 1}: ${question.text}. Tu respuesta: ${responses[index] || "Sin respuesta"}. `;
    });
    
    summaryText += '¬øDeseas enviar estas respuestas o volver para revisar alguna?';
    
    speakText(summaryText, () => {
      // Escuchar confirmaci√≥n del usuario
      listenForConfirmation();
    });
  };
  
  // Escuchar la confirmaci√≥n del usuario
  const listenForConfirmation = () => {
    if (!voiceEnabled) return;
    
    audioService.init('es-ES');
    
    audioService.onResult((transcript, isFinal) => {
      // Mostrar lo que est√° diciendo el usuario
      setCurrentResponse(transcript);
    });
    
    audioService.onEnd((finalTranscript) => {
      setIsListening(false);
      
      // Analizar respuesta para determinar si es confirmaci√≥n o no
      const lowerResponse = finalTranscript.toLowerCase();
      if (
        lowerResponse.includes('s√≠') || 
        lowerResponse.includes('si') || 
        lowerResponse.includes('confirmar') || 
        lowerResponse.includes('enviar') ||
        lowerResponse.includes('aceptar') ||
        lowerResponse.includes('est√° bien')
      ) {
        // Confirmado - enviar respuestas
        speakText('Perfecto, enviando tus respuestas.', () => {
          handleSubmit();
        });
      } else if (
        lowerResponse.includes('no') || 
        lowerResponse.includes('volver') || 
        lowerResponse.includes('revisar') ||
        lowerResponse.includes('regresar')
      ) {
        // Volver a revisar
        setShowConfirmation(false);
        speakText('De acuerdo, volvamos a revisar las preguntas. Puedes navegar entre ellas usando los botones de anterior y siguiente.', null);
      } else {
        // Respuesta no clara
        speakText('No he entendido si deseas confirmar o revisar. Por favor, dime "confirmar" para enviar las respuestas o "revisar" para volver a las preguntas.', () => {
          listenForConfirmation();
        });
      }
    });
    
    audioService.onError(() => {
      setIsListening(false);
      speakText('Hubo un error al escuchar tu confirmaci√≥n. Por favor, usa los botones en pantalla para confirmar o volver.', null);
    });
    
    audioService.start();
    setIsListening(true);
    setConversationState('listening');
    setConversationMessage('Esperando confirmaci√≥n...');
  };
  
  // Manejar el cambio de respuesta manual
  const handleResponseChange = (e) => {
    setCurrentResponse(e.target.value);
  };
  
  // Guardar la respuesta actual y avanzar a la siguiente pregunta manualmente
  const handleNextQuestion = () => {
    // Cancelar cualquier s√≠ntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();
    
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = currentResponse;
    setResponses(updatedResponses);
    
    if (currentQuestionIndex < survey.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setCurrentResponse('');
      
      // Si est√° habilitada la voz, hablar la siguiente pregunta
      if (voiceEnabled) {
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 500);
      }
    } else {
      setShowConfirmation(true);
      if (voiceEnabled) {
        speakText('Hemos terminado todas las preguntas. Ahora te mostrar√© un resumen de tus respuestas para que las confirmes.', () => {
          speakConfirmationSummary();
        });
      }
    }
  };
  
  // Volver a la pregunta anterior manualmente
  const handlePreviousQuestion = () => {
    // Cancelar cualquier s√≠ntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();
    
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
      setCurrentResponse(responses[currentQuestionIndex - 1]);
      
      // Si est√° habilitada la voz, hablar la pregunta anterior
      if (voiceEnabled) {
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 500);
      }
    }
  };
  
  // Enviar todas las respuestas
  const handleSubmit = async () => {
    setIsSubmitting(true);
    
    try {
      // Preparar objeto de respuesta
      const responseData = {
        surveyId: survey._id,
        responses: survey.questions.map((question, index) => ({
          questionId: question._id,
          questionText: question.text,
          responseText: responses[index] || '',
          questionType: question.type
        }))
      };
      
      // Enviar respuesta a la API
      await ResponseService.submitResponse(responseData);
      
      // Mensaje de despedida
      if (voiceEnabled) {
        speakText(survey.farewell || '¬°Gracias por completar la encuesta!', () => {
          // Redirigir a p√°gina de agradecimiento
          navigate(`/thank-you`, { 
            state: { 
              message: survey.farewell || '¬°Gracias por completar la encuesta!' 
            } 
          });
        });
      } else {
        // Redirigir a p√°gina de agradecimiento sin hablar
        navigate(`/thank-you`, { 
          state: { 
            message: survey.farewell || '¬°Gracias por completar la encuesta!' 
          } 
        });
      }
    } catch (error) {
      console.error('Error al enviar respuestas:', error);
      setError('Ocurri√≥ un error al enviar tus respuestas. Por favor, intenta nuevamente.');
      setIsSubmitting(false);
      
      if (voiceEnabled) {
        speakText('Ha ocurrido un error al enviar tus respuestas. Por favor, intenta nuevamente.', null);
      }
    }
  };
  
  // Iniciar reconocimiento de voz manual
  const startVoiceRecognition = () => {
    if (isListening) return;
    
    // Cancelar cualquier s√≠ntesis en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    
    startListening();
  };
  
  // Detener reconocimiento de voz manual
  const stopVoiceRecognition = () => {
    audioService.stop();
    setIsListening(false);
    setConversationState('idle');
  };
  
  // Alternar el uso de voz
  const toggleVoice = () => {
    // Cancelar cualquier s√≠ntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();
    
    const newVoiceEnabled = !voiceEnabled;
    setVoiceEnabled(newVoiceEnabled);
    
    if (newVoiceEnabled && !conversationActive.current) {
      // Si se activa la voz, iniciar conversaci√≥n
      conversationActive.current = false; // Reset para que startConversation funcione
      startConversation();
    }
  };
  
  // Renderizar pantalla de carga
  if (loading) {
    return <LoadingSpinner />;
  }
  
  // Renderizar mensaje de error
  if (error) {
    return <ErrorMessage message={error} />;
  }
  
  // Si no hay encuesta
  if (!survey) {
    return <ErrorMessage message="No se encontr√≥ la encuesta solicitada." />;
  }
  
  // Obtener la pregunta actual
  const currentQuestion = survey.questions[currentQuestionIndex];
  
  return (
    <div className="max-w-3xl mx-auto p-4">
      {/* Control de voz */}
      <div className="flex justify-end mb-2">
        <button 
          onClick={toggleVoice} 
          className={`px-3 py-1 rounded text-sm ${voiceEnabled ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-700'}`}
        >
          {voiceEnabled ? 'üîä Voz Activada' : 'üîá Voz Desactivada'}
        </button>
      </div>
      
      {/* Estado de la conversaci√≥n */}
      {voiceEnabled && conversationState !== 'idle' && (
        <div className={`mb-4 p-2 rounded text-center ${
          conversationState === 'speaking' ? 'bg-blue-100 text-blue-700' : 
          conversationState === 'listening' ? 'bg-green-100 text-green-700' : 
          'bg-yellow-100 text-yellow-700'
        }`}>
          <p className="font-semibold">
            {conversationState === 'speaking' && 'üîä '} 
            {conversationState === 'listening' && 'üé§ '}
            {conversationState === 'processing' && '‚è≥ '}
            {conversationMessage}
          </p>
          {isListening && (
            <p className="text-sm mt-1">Habla tu respuesta claramente...</p>
          )}
        </div>
      )}
      
      {/* Encabezado de la encuesta */}
      <div className="mb-8 text-center">
        <h1 className="text-3xl font-bold mb-2">{survey.title}</h1>
        <p className="text-gray-600">{survey.description}</p>
        {currentQuestionIndex === 0 && survey.welcomeMessage && (
          <div className="mt-4 p-4 bg-blue-50 rounded-lg">
            <p>{survey.welcomeMessage}</p>
          </div>
        )}
      </div>
      
      {/* Progreso */}
      <div className="mb-6">
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className="bg-blue-600 h-2.5 rounded-full" 
            style={{ width: `${((currentQuestionIndex + 1) / survey.questions.length) * 100}%` }}
          ></div>
        </div>
        <p className="text-right text-sm mt-1">
          Pregunta {currentQuestionIndex + 1} de {survey.questions.length}
        </p>
      </div>
      
      {/* Pantalla de confirmaci√≥n */}
      {showConfirmation ? (
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold mb-4">Confirmar env√≠o</h2>
          <p className="mb-4">¬øEst√°s seguro de que deseas enviar tus respuestas?</p>
          
          <div className="mb-6">
            <h3 className="font-bold mb-2">Resumen de respuestas:</h3>
            {survey.questions.map((question, index) => (
              <div key={question._id} className="mb-3">
                <p className="font-medium">{index + 1}. {question.text}</p>
                <p className="text-gray-700 pl-4">{responses[index] || "(Sin respuesta)"}</p>
              </div>
            ))}
          </div>
          
          {isListening && (
            <div className="bg-green-100 p-3 rounded mb-4 animate-pulse">
              <p className="text-center text-green-800">
                üé§ Di "confirmar" para enviar o "revisar" para volver
              </p>
              {currentResponse && (
                <p className="text-sm italic text-center mt-1">"{currentResponse}"</p>
              )}
            </div>
          )}
          
          <div className="flex justify-between">
            <button
              onClick={() => {
                setShowConfirmation(false);
                if (voiceEnabled) {
                  speakText('Volvamos a revisar las preguntas.', null);
                }
              }}
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
              disabled={isSubmitting}
            >
              Volver
            </button>
            <button
              onClick={handleSubmit}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Enviando...' : 'Confirmar env√≠o'}
            </button>
          </div>
        </div>
      ) : (
        <>
          {/* Pregunta actual */}
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-bold mb-4">{currentQuestion.text}</h2>
            
            {/* Opciones para preguntas de tipo opci√≥n */}
            {currentQuestion.type === 'multiple_choice' && currentQuestion.options && (
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">Opciones:</p>
                <ul className="list-disc pl-5">
                  {currentQuestion.options.map((option, idx) => (
                    <li key={idx} className="mb-1">{option}</li>
                  ))}
                </ul>
              </div>
            )}
            
            {/* √Årea de respuesta */}
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tu respuesta:
              </label>
              <textarea
                value={currentResponse}
                onChange={handleResponseChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Escribe tu respuesta..."
              ></textarea>
              
              {/* Controles de reconocimiento de voz */}
              <div className="flex items-center mt-2">
                {!isListening ? (
                  <button
                    onClick={startVoiceRecognition}
                    className="flex items-center px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                  >
                    <span className="mr-1">üé§</span> Responder por voz
                  </button>
                ) : (
                  <button
                    onClick={stopVoiceRecognition}
                    className="flex items-center px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600 animate-pulse"
                  >
                    <span className="mr-1">‚èπÔ∏è</span> Detener grabaci√≥n
                  </button>
                )}
                {isListening && (
                  <span className="ml-2 text-red-500 animate-pulse">Escuchando...</span>
                )}
              </div>
            </div>
          </div>
          
          {/* Botones de navegaci√≥n */}
          <div className="flex justify-between">
            <button
              onClick={handlePreviousQuestion}
              disabled={currentQuestionIndex === 0}
              className={`px-4 py-2 rounded ${
                currentQuestionIndex === 0
                  ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
            >
              Anterior
            </button>
            
            <button
              onClick={handleNextQuestion}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              {currentQuestionIndex < survey.questions.length - 1 ? 'Siguiente' : 'Finalizar'}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TakeSurvey;
