  // Función para extraer un nombre de texto
  const extractName = (text) => {
    console.log('Extrayendo nombre de:', text);

    // Si el texto está vacío o no es una cadena
    if (!text || typeof text !== 'string') {
      console.log('Texto vacío o no es cadena, usando nombre genérico');
      return 'Estimado participante';
    }

    // Eliminar signos de puntuación y normalizar texto
    const normalizedText = text.replace(/[.,;:¡!¿?]/g, '').trim();

    // Frases comunes al indicar un nombre
    const namePhrases = [
      'me llamo', 'mi nombre es', 'soy', 'yo soy', 
      'puedes llamarme', 'puede llamarme', 'llámame', 'llamame'
    ];

    // Buscar frases de presentación
    let cleanedText = normalizedText;
    for (const phrase of namePhrases) {
      if (normalizedText.toLowerCase().includes(phrase)) {
        // Extraer texto después de la frase de presentación
        const parts = normalizedText.toLowerCase().split(phrase);
        if (parts.length > 1 && parts[1].trim()) {
          cleanedText = parts[1].trim();
          console.log(`Frase de presentación '${phrase}' encontrada, texto limpio: '${cleanedText}'`);
          break;
        }
      }
    }

    // Palabras comunes para ignorar al final
    const commonWords = ['gracias', 'por favor', 'aquí', 'señor', 'señora', 'mucho gusto'];
    for (const word of commonWords) {
      if (cleanedText.toLowerCase().endsWith(word)) {
        cleanedText = cleanedText.slice(0, -(word.length)).trim();
      }
    }

    // Si el texto limpio está vacío, usar el original
    if (!cleanedText) {
      cleanedText = normalizedText;
    }

    // Obtener las palabras del texto
    const words = cleanedText.split(/\s+/);

    // Si solo hay una palabra, asumimos que es el nombre
    if (words.length === 1) {
      const name = words[0];
      console.log('Nombre extraído (una palabra):', name);
      // Capitalizar primera letra
      return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    }

    // Si hay más palabras, tomar hasta 3 palabras como nombre completo
    const nameWords = words.slice(0, Math.min(3, words.length));
    let extractedName = nameWords.join(' ');

    // Capitalizar cada palabra
    extractedName = extractedName.split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(' ');

    console.log('Nombre extraído (múltiples palabras):', extractedName);
    return extractedName;
  };

  // Función para guardar respuesta y pasar a la siguiente pregunta
  const saveResponse = (value) => {
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = {
      value,
      timestamp: new Date().toISOString(),
      index: currentQuestionIndex
    };
    setResponses(updatedResponses);

    // Verificar si hay más preguntas
    if (currentQuestionIndex < survey.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Fin de la encuesta
      setCurrentStep('thanks');
    }
  };

  // Manejar clic en botón de micrófono
  const handleMicrophoneClick = () => {
    if (isListening) {
      setIsListening(false);
    } else {
      startListening();
    }
  };

  // Manejar clic en botones de opciones 
  const handleOptionClick = (value) => {
    if (isListening) return;

    processResponse(value);
  };

  // Solicitar permisos de micrófono
  const requestMicrophonePermission = async () => {
    try {
      const permission = await checkMicrophonePermission();
      setMicrophonePermission(permission);

      if (permission) {
        // Iniciar la encuesta si se conceden los permisos
        setCurrentStep('welcome');
      } else {
        setErrorMessage('No se pudo obtener permiso para el micrófono. Por favor, permita el acceso en la configuración de su navegador.');
      }
    } catch (error) {
      console.error('Error al solicitar permisos de micrófono:', error);
      setErrorMessage('Error al solicitar permisos de micrófono.');
    }
  };

  // Función centralizada para solicitar síntesis de voz
  const speakText = async (text) => {
    if (!text) return Promise.resolve();
    console.log('Solicitando síntesis de voz para:', text);

    // Evitar interrupción de síntesis en curso y garantizar una experiencia fluida
    return new Promise((resolve) => {
      setVoiceText(text);
      setIsSpeaking(true);

      // Configurar callback que se ejecutará cuando termine la síntesis
      setSpeakEndCallback(() => {
        console.log('Síntesis de voz completada para:', text);
        setIsSpeaking(false);
        setSpeakEndCallback(null);
        resolve();
      });
    });
  };

  useEffect(() => {
    setStartTime(new Date());
  }, []);

  // Función para repetir la pregunta actual
  const repeatQuestion = async () => {
    try {
      if (currentStep === 'questions' && survey && survey.questions) {
        // Detener cualquier síntesis o escucha en curso
        if (isListening) {
          // Detener escucha si está activa
          setIsListening(false);
        }

        const currentQuestion = survey.questions[currentQuestionIndex];
        if (currentQuestion) {
          // Construir el mensaje de la pregunta
          const questionText = `La pregunta es: ${currentQuestion.text}`;

          // Hablar la pregunta
          await speakText(questionText);

          // Esperar un momento antes de comenzar a escuchar
          await new Promise(resolve => setTimeout(resolve, 1000));

          // Iniciar escucha para la respuesta
          startListening();
        }
      }
    } catch (error) {
      console.error('Error al repetir la pregunta:', error);
      setErrorMessage('Ocurrió un error al repetir la pregunta.');
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-4">
      {loading ? (
        <div className="flex justify-center my-12">
          <FaSpinner className="animate-spin h-12 w-12 text-blue-500" />
          <span className="sr-only">Cargando...</span>
        </div>
      ) : errorMessage ? (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded my-4">
          <p>{errorMessage}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="mt-2 bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            Reintentar
          </button>
        </div>
      ) : currentStep === 'thanks' ? (
        <div className="text-center my-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">¡Gracias por completar la encuesta!</h2>
          <p className="text-lg text-gray-600 mb-8">Sus respuestas han sido registradas correctamente.</p>
          <button
            onClick={() => navigate('/surveys')}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Volver al inicio
          </button>
        </div>
      ) : (
        <div className="bg-white shadow-md rounded-lg p-6">
          <h1 className="text-2xl font-bold text-center mb-6">{survey?.title}</h1>

          {/* Mostrar estado actual */}
          <div className="mb-4 p-2 bg-gray-100 rounded">
            {currentStep === 'initial' && <p>Iniciando encuesta...</p>}
            {currentStep === 'name' && <p>Por favor, diga su nombre</p>}
            {currentStep === 'questions' && (
              <p>
                Pregunta {currentQuestionIndex + 1} de {survey?.questions?.length || 0}
              </p>
            )}
          </div>

          {/* Mostrar pregunta actual o solicitud de nombre */}
          <div className="my-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            {currentStep === 'name' ? (
              <h2 className="text-xl font-semibold text-blue-800">
                Por favor, diga su nombre
              </h2>
            ) : currentStep === 'questions' && survey?.questions ? (
              <div>
                <h2 className="text-xl font-semibold text-blue-800">
                  {survey.questions[currentQuestionIndex]?.text}
                </h2>
                {survey.questions[currentQuestionIndex]?.options && (
                  <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-3">
                    {survey.questions[currentQuestionIndex].options.map((option, index) => (
                      <button
                        key={index}
                        onClick={() => handleOptionClick(option)}
                        className="bg-white border border-blue-300 p-3 rounded hover:bg-blue-100 transition"
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ) : null}
          </div>

          {/* Controles de voz manuales */}
          <div className="my-6 flex justify-center space-x-4">
            <button 
              onClick={() => startListening()}
              disabled={isSpeaking || isListening}
              className={`p-4 rounded-full ${isListening 
                ? 'bg-red-500 text-white animate-pulse' 
                : 'bg-blue-500 text-white hover:bg-blue-600'}`}
              title="Empezar a escuchar"
            >
              <FaMicrophone className="h-6 w-6" />
              <span className="sr-only">Escuchar</span>
            </button>

            {transcript && (
              <button 
                onClick={() => processResponse(transcript)}
                className="p-4 rounded-full bg-green-500 text-white hover:bg-green-600"
                title="Confirmar respuesta"
              >
                <FaCheckCircle className="h-6 w-6" />
                <span className="sr-only">Confirmar</span>
              </button>
            )}

            {currentStep === 'questions' && (
              <button 
                onClick={() => repeatQuestion()}
                className="p-4 rounded-full bg-yellow-500 text-white hover:bg-yellow-600"
                title="Repetir pregunta"
              >
                <FaVolumeUp className="h-6 w-6" />
                <span className="sr-only">Repetir</span>
              </button>
            )}
          </div>

          {/* Mostrar la transcripción */}
          {transcript && (
            <div className="my-4 p-3 bg-gray-100 rounded">
              <p className="text-lg">
                <span className="font-semibold">Su respuesta:</span> {transcript}
              </p>
            </div>
          )}

          {/* Estado del reconocimiento */}
          <div className="my-2 text-center">
            {isListening && (
              <p className="text-sm text-blue-500 animate-pulse flex items-center justify-center">
                <span className="mr-2">Escuchando</span>
                <span className="flex space-x-1">
                  <span className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></span>
                  <span className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></span>
                  <span className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></span>
                </span>
              </p>
            )}
            {isSpeaking && (
              <p className="text-sm text-green-500 animate-pulse flex items-center justify-center">
                <FaVolumeUp className="mr-2" />
                Hablando...
              </p>
            )}
          </div>

          {/* Instrucciones para el usuario */}
          <div className="mt-10 text-sm text-gray-600 bg-gray-50 p-4 rounded">
            <h3 className="font-bold mb-2">Instrucciones:</h3>
            <ul className="list-disc pl-5 space-y-1">
              <li>Hable con claridad para que el sistema pueda entenderle correctamente</li>
              <li>Puede usar los botones de control para una experiencia más fluida</li>
              <li>Si tiene problemas, haga clic en el botón del micrófono para reintentar</li>
            </ul>
          </div>
        </div>
      )}

      {/* Componente de voz */}
      {isSpeaking && (
        <SimpleVoice
          text={voiceText}
          onEnd={speakEndCallback}
          autoSpeak={true}
        />
      )}
    </div>
  );
};

export default TakeSurvey;
