import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';

// Register Chart.js components
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

// Mock data - would be replaced with API calls in production
const mockSurveys = [
  {
    id: 'survey-1',
    title: 'Satisfacción del Cliente',
    description: 'Encuesta para medir la satisfacción con nuestros servicios',
    createdAt: new Date('2025-04-01').toISOString(),
    questions: [
      {
        id: 'q1',
        text: '¿Cómo calificaría nuestro servicio?',
        type: 'rating',
        responses: [
          { value: '5', count: 10 },
          { value: '4', count: 8 },
          { value: '3', count: 4 },
          { value: '2', count: 1 },
          { value: '1', count: 1 }
        ]
      },
      {
        id: 'q2',
        text: '¿Recomendaría nuestro servicio a otros?',
        type: 'yesno',
        responses: [
          { value: 'Sí', count: 18 },
          { value: 'No', count: 6 }
        ]
      },
      {
        id: 'q3',
        text: '¿Qué aspectos podríamos mejorar?',
        type: 'open',
        responses: [
          { value: 'Atención más rápida', count: 1 },
          { value: 'Mejores precios', count: 1 },
          { value: 'Mayor variedad de productos', count: 1 }
        ]
      }
    ],
    totalResponses: 24
  },
  {
    id: 'survey-2',
    title: 'Experiencia de Usuario',
    description: 'Evaluación de la experiencia con nuestra nueva plataforma',
    createdAt: new Date('2025-04-15').toISOString(),
    questions: [
      {
        id: 'q1',
        text: '¿Fue fácil navegar por nuestra plataforma?',
        type: 'yesno',
        responses: [
          { value: 'Sí', count: 9 },
          { value: 'No', count: 3 }
        ]
      },
      {
        id: 'q2',
        text: '¿Qué características le gustaría ver en nuestra plataforma?',
        type: 'open',
        responses: [
          { value: 'Modo oscuro', count: 1 },
          { value: 'Más opciones de personalización', count: 1 },
          { value: 'Mejor búsqueda', count: 1 }
        ]
      }
    ],
    totalResponses: 12
  },
  {
    id: 'survey-3',
    title: 'Feedback de Producto',
    description: 'Recopilación de opiniones sobre nuestro último lanzamiento',
    createdAt: new Date('2025-05-01').toISOString(),
    questions: [
      {
        id: 'q1',
        text: '¿Cómo calificaría nuestro nuevo producto?',
        type: 'rating',
        responses: [
          { value: '5', count: 3 },
          { value: '4', count: 2 },
          { value: '3', count: 2 },
          { value: '2', count: 1 },
          { value: '1', count: 0 }
        ]
      }
    ],
    totalResponses: 8
  }
];

const SurveyDetail = () => {
  const { surveyId } = useParams();
  const [survey, setSurvey] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('results');

  useEffect(() => {
    // This would be replaced with an actual API call
    const fetchSurvey = async () => {
      try {
        // Simulate API call
        setTimeout(() => {
          const foundSurvey = mockSurveys.find(s => s.id === surveyId);
          setSurvey(foundSurvey || null);
          setLoading(false);
        }, 1000);
      } catch (error) {
        console.error('Error fetching survey:', error);
        setLoading(false);
      }
    };

    fetchSurvey();
  }, [surveyId]);

  // Format date in a readable format
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('es-ES', options);
  };

  // Prepare chart data for ratings
  const prepareRatingChartData = (responses) => {
    return {
      labels: ['1 ⭐', '2 ⭐', '3 ⭐', '4 ⭐', '5 ⭐'],
      datasets: [
        {
          label: 'Número de Respuestas',
          data: [
            responses.find(r => r.value === '1')?.count || 0,
            responses.find(r => r.value === '2')?.count || 0,
            responses.find(r => r.value === '3')?.count || 0,
            responses.find(r => r.value === '4')?.count || 0,
            responses.find(r => r.value === '5')?.count || 0
          ],
          backgroundColor: [
            'rgba(255, 99, 132, 0.6)',
            'rgba(255, 159, 64, 0.6)',
            'rgba(255, 205, 86, 0.6)',
            'rgba(75, 192, 192, 0.6)',
            'rgba(54, 162, 235, 0.6)'
          ],
          borderColor: [
            'rgb(255, 99, 132)',
            'rgb(255, 159, 64)',
            'rgb(255, 205, 86)',
            'rgb(75, 192, 192)',
            'rgb(54, 162, 235)'
          ],
          borderWidth: 1
        }
      ]
    };
  };

  // Prepare chart data for yes/no questions
  const prepareYesNoChartData = (responses) => {
    return {
      labels: ['Sí', 'No'],
      datasets: [
        {
          data: [
            responses.find(r => r.value === 'Sí')?.count || 0,
            responses.find(r => r.value === 'No')?.count || 0
          ],
          backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)'],
          borderColor: ['rgb(75, 192, 192)', 'rgb(255, 99, 132)'],
          borderWidth: 1
        }
      ]
    };
  };

  if (loading) {
    return (
      <div className="flex justify-center my-12">
        <svg className="animate-spin h-8 w-8 text-primary-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
      </div>
    );
  }

  if (!survey) {
    return (
      <div className="card text-center py-12">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
        </svg>
        <h3 className="text-lg font-medium text-gray-800 mb-2">Encuesta no encontrada</h3>
        <p className="text-gray-600 mb-6">La encuesta que estás buscando no existe o ha sido eliminada.</p>
        <Link to="/dashboard" className="btn btn-primary">
          Volver al Dashboard
        </Link>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold">{survey.title}</h1>
          <p className="text-gray-600">Creada el {formatDate(survey.createdAt)}</p>
        </div>
        <div className="flex space-x-3 mt-4 md:mt-0">
          <Link to={`/take-survey/${survey.id}`} className="btn btn-primary flex items-center" target="_blank" rel="noopener noreferrer">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
            </svg>
            Vista Previa
          </Link>
          <button className="btn btn-outline flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
            </svg>
            Exportar
          </button>
        </div>
      </div>

      {/* Description */}
      <div className="card mb-6">
        <div className="flex items-start">
          <div className="flex-grow">
            <h2 className="text-lg font-semibold mb-2">Descripción</h2>
            <p className="text-gray-700">{survey.description || 'No hay descripción disponible'}</p>
          </div>
          <div className="bg-primary-100 text-primary-800 rounded-lg p-3">
            <div className="text-center">
              <div className="text-3xl font-bold">{survey.totalResponses}</div>
              <div className="text-sm">Respuestas</div>
            </div>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200 mb-6">
        <nav className="flex space-x-8">
          <button
            className={`py-4 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'results'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
            onClick={() => setActiveTab('results')}
          >
            Resultados
          </button>
          <button
            className={`py-4 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'questions'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
            onClick={() => setActiveTab('questions')}
          >
            Preguntas
          </button>
          <button
            className={`py-4 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'share'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
            onClick={() => setActiveTab('share')}
          >
            Compartir
          </button>
        </nav>
      </div>

      {/* Tab content */}
      {activeTab === 'results' && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Resultados de la Encuesta</h2>

          {survey.questions.map((question, index) => (
            <div key={question.id} className="card mb-6">
              <h3 className="text-lg font-medium mb-4">
                {index + 1}. {question.text}
              </h3>

              {question.type === 'rating' && (
                <div className="h-64">
                  <Bar 
                    data={prepareRatingChartData(question.responses)}
                    options={{
                      responsive: true,
                      maintainAspectRatio: false,
                      scales: {
                        y: {
                          beginAtZero: true,
                          ticks: {
                            precision: 0
                          }
                        }
                      }
                    }}
                  />
                </div>
              )}

              {question.type === 'yesno' && (
                <div className="h-64">
                  <Pie 
                    data={prepareYesNoChartData(question.responses)}
                    options={{
                      responsive: true,
                      maintainAspectRatio: false,
                      plugins: {
                        legend: {
                          position: 'bottom'
                        }
                      }
                    }}
                  />
                </div>
              )}

              {question.type === 'open' && (
                <div>
                  <h4 className="text-md font-medium mb-2">Respuestas:</h4>
                  {question.responses.length > 0 ? (
                    <ul className="list-disc pl-5 space-y-1">
                      {question.responses.map((response, idx) => (
                        <li key={idx} className="text-gray-700">{response.value}</li>
                      ))}
                    </ul>
                  ) : (
                    <p className="text-gray-500 italic">No hay respuestas disponibles</p>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {activeTab === 'questions' && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Preguntas de la Encuesta</h2>

          {survey.questions.map((question, index) => (
            <div key={question.id} className="card mb-4">
              <div className="flex justify-between">
                <h3 className="text-lg font-medium">
                  {index + 1}. {question.text}
                </h3>
                <span className="text-sm bg-gray-100 text-gray-700 px-2 py-1 rounded">
                  {question.type === 'open' ? 'Respuesta abierta' : 
                   question.type === 'rating' ? 'Calificación (1-5)' : 
                   question.type === 'yesno' ? 'Sí/No' : 
                   question.type === 'single' ? 'Selección única' : 
                   'Selección múltiple'}
                </span>
              </div>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'share' && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Compartir Encuesta</h2>

          <div className="card mb-6">
            <h3 className="text-lg font-medium mb-4">Enlace para compartir</h3>
            <div className="flex">
              <input
                type="text"
                value={`https://encuestas-por-voz.com/take-survey/${survey.id}`}
                readOnly
                className="input flex-grow"
              />
              <button className="btn btn-primary ml-2">
                Copiar
              </button>
            </div>
            <p className="mt-2 text-sm text-gray-600">
              Comparte este enlace con las personas que deseas que completen la encuesta.
            </p>
          </div>

          <div className="card">
            <h3 className="text-lg font-medium mb-4">Código QR</h3>
            <div className="flex flex-col items-center">
              <div className="bg-white p-4 border rounded-lg mb-4">
                {/* This would be a real QR code in production */}
                <div className="w-48 h-48 bg-gray-200 flex items-center justify-center">
                  <p className="text-gray-500 text-sm text-center">
                    Aquí se mostraría el código QR de la encuesta
                  </p>
                </div>
              </div>
              <button className="btn btn-outline">
                Descargar Código QR
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SurveyDetail;
