/**
 * Servicio de síntesis de voz optimizado para navegadores
 * Implementa técnicas avanzadas para mejorar la confiabilidad de la API de Speech Synthesis
 */

// Cache de voces para evitar búsquedas repetidas
let cachedVoices = null;

/**
 * Obtiene todas las voces disponibles en el navegador
 */
const getVoices = () => {
  if (cachedVoices) return cachedVoices;
  return window.speechSynthesis.getVoices();
};

/**
 * Inicializa el sistema de síntesis y precarga las voces
 * Esto debe llamarse al inicio de la aplicación
 */
export const initSpeechSystem = () => {
  // Verificar si la API está disponible
  if (!window.speechSynthesis) {
    console.error('Speech Synthesis no está disponible en este navegador');
    return false;
  }

  // Obtener y cachear las voces disponibles
  const loadVoices = () => {
    const voices = window.speechSynthesis.getVoices();
    if (voices.length > 0) {
      cachedVoices = voices;

      // Log información sobre las voces disponibles
      console.log(`AudioService: ${voices.length} voces disponibles`);

      // Buscar voces en español
      const spanishVoices = voices.filter(v => v.lang.includes('es') || v.name.toLowerCase().includes('spanish'));
      if (spanishVoices.length > 0) {
        console.log('AudioService: Voces en español disponibles:', spanishVoices.map(v => `${v.name} (${v.lang})`).join(', '));
      } else {
        console.warn('AudioService: No se encontraron voces en español');
      }
    }
  };

  // Chrome requiere este evento
  if (window.speechSynthesis.onvoiceschanged !== undefined) {
    window.speechSynthesis.onvoiceschanged = loadVoices;
  }

  // Intentar cargar las voces inmediatamente también
  loadVoices();
  return true;
};

/**
 * Habla el texto dado usando técnicas avanzadas para mejorar la confiabilidad
 */
export const speakText = (text, onStarted, onEnded, onError) => {
  if (!window.speechSynthesis) {
    if (onError) onError('Speech Synthesis no está disponible en este navegador');
    return () => {};
  }
  if (!text || text.trim() === '') {
    if (onError) onError('Texto vacío');
    return () => {};
  }
  try {
    // Limpiar cualquier síntesis en curso
    window.speechSynthesis.cancel();

    // Crear el objeto de síntesis
    const utterance = new SpeechSynthesisUtterance(text);

    // Configurar parámetros básicos
    utterance.lang = 'es-ES';
    utterance.rate = 1.0; // Velocidad normal
    utterance.pitch = 1.0; // Tono normal
    utterance.volume = 1.0; // Volumen máximo

    // Obtener voces y seleccionar una voz en español si está disponible
    const voices = getVoices();
    const spanishVoice = voices.find(voice => voice.lang.includes('es') || voice.name.toLowerCase().includes('spanish'));
    if (spanishVoice) {
      console.log(`AudioService: Usando voz ${spanishVoice.name}`);
      utterance.voice = spanishVoice;
    } else if (voices.length > 0) {
      console.log(`AudioService: No hay voces en español, usando ${voices[0].name}`);
      utterance.voice = voices[0];
    }

    // Configurar eventos
    utterance.onstart = () => {
      console.log('AudioService: Síntesis iniciada');
      if (onStarted) onStarted();
    };
    utterance.onend = () => {
      console.log('AudioService: Síntesis completada');
      if (onEnded) onEnded();
    };
    utterance.onerror = event => {
      console.error('AudioService: Error en síntesis:', event);
      if (onError) onError(event.error || 'Error desconocido');
    };

    // Iniciar síntesis
    window.speechSynthesis.speak(utterance);

    // *** WORKAROUNDS CRÍTICOS ***

    // 1. Workaround para el problema de Chrome donde la síntesis se detiene después de ~15s
    const resumeInterval = setInterval(() => {
      if (window.speechSynthesis.speaking) {
        console.log('AudioService: Aplicando workaround de pausa/resume');
        window.speechSynthesis.pause();
        window.speechSynthesis.resume();
      } else {
        clearInterval(resumeInterval);
      }
    }, 10000);

    // 2. Workaround para el caso donde onend no se dispara
    const maxSpeakingTime = Math.max(5000, text.length * 80); // ~80ms por caracter
    const timeoutId = setTimeout(() => {
      if (window.speechSynthesis.speaking) {
        console.log(`AudioService: Detectado posible bloqueo después de ${maxSpeakingTime}ms, forzando finalización`);
        window.speechSynthesis.cancel();
        if (onEnded) onEnded();
      }
      clearInterval(resumeInterval);
    }, maxSpeakingTime);

    // Función para detener la síntesis
    return () => {
      clearTimeout(timeoutId);
      clearInterval(resumeInterval);
      window.speechSynthesis.cancel();
    };
  } catch (error) {
    console.error('AudioService: Error general:', error);
    if (onError) onError(error.message || 'Error desconocido');
    return () => {};
  }
};

/**
 * Servicio para manejar la funcionalidad de reconocimiento de voz
 */

class AudioService {
  constructor() {
    this.recognition = null;
    this.isSupported = 'webkitSpeechRecognition' in window || 'SpeechRecognition' in window;
    this.isListening = false;
    this.transcript = '';
    this.onResultCallback = null;
    this.onEndCallback = null;
    this.onErrorCallback = null;
  }

  /**
   * Inicializa el reconocimiento de voz
   * @param {string} language - Idioma para el reconocimiento (por defecto es español)
