import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  isSpeechRecognitionSupported,
  listenForSpeech,
  checkMicrophonePermission
} from '../utils/voiceSurveyAPI';
import { SurveyService, ResponseService } from '../services/apiService';
import { FaSpinner, FaMicrophone, FaVolumeUp, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';
import NLPService from '../services/nlpService';
import AudioService from '../services/audioService';

// Componentes
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';

const TakeSurvey = () => {
  const { surveyId } = useParams();
  const navigate = useNavigate();
  const [survey, setSurvey] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentStep, setCurrentStep] = useState('initial'); // 'initial', 'name', 'questions', 'thanks'
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState([]);
  const [userName, setUserName] = useState('');
  const [transcript, setTranscript] = useState('');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [browserSupported, setBrowserSupported] = useState(true);
  const [microphonePermission, setMicrophonePermission] = useState(null);
  const [showThankYouMessage, setShowThankYouMessage] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [speakEndCallback, setSpeakEndCallback] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [currentResponse, setCurrentResponse] = useState('');

  // Conjunto de frases adaptativas clasificadas por tono para mejorar la naturalidad
  const ADAPTIVE_PHRASES = {
    acknowledgments: [
      { text: 'Gracias por su respuesta.', tone: 'neutral' },
      { text: '¡Excelente! He registrado su respuesta.', tone: 'positive' },
      { text: 'Perfecto, continuemos.', tone: 'positive' },
      { text: 'Entendido, muchas gracias.', tone: 'neutral' },
      { text: 'He anotado su respuesta.', tone: 'neutral' },
      { text: '¡Genial! Avancemos.', tone: 'positive' },
      { text: 'Comprendo, continuamos.', tone: 'supportive' },
      { text: 'Gracias por compartir su opinión.', tone: 'supportive' }
    ],
    transitions: [
      { text: 'Pasemos a la siguiente pregunta.', tone: 'neutral' },
      { text: 'Continuemos con la siguiente pregunta.', tone: 'neutral' },
      { text: 'Ahora, la siguiente pregunta.', tone: 'neutral' },
      { text: 'Avancemos a la siguiente pregunta.', tone: 'positive' },
      { text: 'Me gustaría preguntarle ahora sobre...', tone: 'supportive' }
    ],
    clarifications: [
      { text: 'Disculpe, no he entendido su respuesta. ¿Podría repetirla?', tone: 'neutral' },
      { text: 'Parece que no pude entender correctamente. ¿Podría decirlo de otra manera?', tone: 'supportive' },
      { text: 'Necesito una respuesta más clara. ¿Podría intentarlo nuevamente?', tone: 'neutral' },
      { text: 'No capté bien su respuesta. ¿Podría hablar un poco más claro?', tone: 'supportive' }
    ],
    greetings: [
      { text: 'Bienvenido a nuestra encuesta. Apreciamos mucho su participación.', tone: 'positive' },
      { text: 'Gracias por participar en esta encuesta. Sus opiniones son muy importantes.', tone: 'neutral' },
      { text: 'Le damos la bienvenida a esta encuesta. Valoramos su tiempo y sus respuestas.', tone: 'supportive' }
    ],
    farewells: [
      { text: '¡Muchas gracias por completar la encuesta! Sus respuestas son muy valiosas para nosotros.', tone: 'positive' },
      { text: 'La encuesta ha finalizado. Agradecemos su participación y su tiempo.', tone: 'neutral' },
      { text: 'Hemos llegado al final de la encuesta. Realmente apreciamos sus opiniones.', tone: 'supportive' }
    ]
  };

  // Comprobar compatibilidad del navegador y permisos
  useEffect(() => {
    const checkCompatibility = async () => {
      // Verificar si el navegador soporta reconocimiento de voz
      const speechRecognitionSupported = isSpeechRecognitionSupported();
      const speechSynthesisSupported = true; // isSpeechSynthesisSupported();

      if (!speechRecognitionSupported || !speechSynthesisSupported) {
        setBrowserSupported(false);
        setErrorMessage('Su navegador no soporta las tecnologías de voz necesarias para esta encuesta. Por favor, use Chrome, Edge o Safari.');
        return;
      }

      // Verificar permisos de micrófono
      const hasMicrophonePermission = await checkMicrophonePermission();
      setMicrophonePermission(hasMicrophonePermission);

      if (!hasMicrophonePermission) {
        setErrorMessage('Necesitamos permiso para usar su micrófono. Por favor, permita el acceso cuando se le solicite.');
      }
    };

    checkCompatibility();
  }, []);

  // Inicialización de la encuesta
  useEffect(() => {
    const fetchSurvey = async () => {
      try {
        // Usar getPublicSurvey en lugar de getSurveyById que no existe
        const surveyData = await SurveyService.getPublicSurvey(surveyId);
        setSurvey(surveyData);
        setLoading(false);
        // Registrar el tiempo de inicio de la encuesta
        setStartTime(new Date());

        // Inicializar array de respuestas vacío
        if (surveyData && surveyData.questions) {
          setResponses(Array(surveyData.questions.length).fill(null).map(() => ({ 
            value: '', 
            timestamp: null,
            index: 0,
            required_clarification: false
          })));
        }
      } catch (error) {
        console.error('Error al obtener encuesta:', error);
        setErrorMessage('No se pudo cargar la encuesta. Por favor, inténtelo de nuevo más tarde.');
        setLoading(false);
      }
    };

    fetchSurvey();
  }, [surveyId]);

  // Iniciar conversación automáticamente cuando la encuesta está cargada
  useEffect(() => {
    if (survey && currentStep === 'initial') {
      startConversation();
    }
  }, [survey, currentStep]);

  // Iniciar la conversación automática
  const startConversation = async () => {
    console.log('Función startConversation ejecutada');
    console.log('Estado actual:', survey);

    try {
      // Obtener la hora del día para personalizar el saludo
      const hour = new Date().getHours();
      let greeting;

      if (hour < 12) {
        greeting = 'Buenos días';
      } else if (hour < 18) {
        greeting = 'Buenas tardes';
      } else {
        greeting = 'Buenas noches';
      }

      // Construir un mensaje de bienvenida completo y natural
      const welcomeMessage = `${greeting}. Bienvenido a nuestra encuesta. Apreciamos mucho su participación. Esta encuesta es sobre: ${survey.title}. ${survey.description || ''} Para comenzar, ¿podría decirme su nombre por favor?`;

      console.log('Enviando mensaje de bienvenida con solicitud de nombre:', welcomeMessage);

      // Cambiar el estado para mostrar que estamos en el paso de solicitar nombre
      setCurrentStep('name');

      // Primero asegurarnos de que no estamos hablando
      if (isSpeaking) {
        await new Promise(resolve => {
          const checkInterval = setInterval(() => {
            if (!isSpeaking) {
              clearInterval(checkInterval);
              resolve();
            }
          }, 100);
        });
      }

      // Hablar el mensaje de bienvenida con solicitud de nombre
      await speakText(welcomeMessage);

      console.log('Mensaje de bienvenida completado');

      // Esperar un momento antes de comenzar a escuchar
      await new Promise(resolve => setTimeout(resolve, 1000));

      console.log('Iniciando reconocimiento de voz para captar nombre...');
      startListening();
    } catch (error) {
      console.error('Error al iniciar conversación:', error);
      setErrorMessage('Ocurrió un error al iniciar la encuesta. Por favor, recargue la página e intente nuevamente.');
    }
  };

  // Mover a la sección de preguntas
  const moveToQuestions = async () => {
    console.log('Moviendo a la sección de preguntas...');
    setCurrentStep('questions');
    setCurrentQuestionIndex(0);
    // Pequeña pausa para asegurar que los estados se actualicen
    await new Promise(resolve => setTimeout(resolve, 500));
    await speakCurrentQuestion();
  };

  // Hablar la pregunta actual
  const speakCurrentQuestion = async () => {
    console.log('Hablando pregunta actual:', currentQuestionIndex);
    if (!survey || currentQuestionIndex >= survey.questions.length) {
      console.error('No hay más preguntas o índice inválido:', {currentQuestionIndex, questionsLength: survey?.questions?.length});
      return;
    }

    const question = survey.questions[currentQuestionIndex];
    console.log('Pregunta a formular:', question);
    let questionText = question.text;

    // Añadir instrucciones específicas según tipo de pregunta
    if (question.type === 'rating') {
      questionText += ' Por favor, responda con un número del 1 al 5, donde 1 es lo más bajo y 5 lo más alto.';
    } else if (question.type === 'yesno') {
      questionText += ' Por favor, responda sí o no.';
    }

    // Hablar la pregunta
    setIsSpeaking(true);
    try {
      await speakText(questionText);
      console.log('Pregunta terminada de hablar, iniciando escucha...');
      setIsSpeaking(false);
      // Pequeña pausa antes de empezar a escuchar
      setTimeout(async () => {
        console.log('Iniciando escucha desde setTimeout después de hablar pregunta');
        await startListening();
      }, 800);
    } catch (error) {
      console.error('Error al hablar la pregunta:', error);
      setIsSpeaking(false);
      // Reintentar en caso de error
      setTimeout(() => speakCurrentQuestion(), 1500);
    }
  };

  // Función para cuando termina de hablar la pregunta
  const onQuestionSpeakEnd = () => {
    setTimeout(startListening, 500);
  };

  // Función para cuando termina de hablar el mensaje de agradecimiento
  const onThankYouSpeakEnd = async () => {
    try {
      // Enviar respuestas al servidor
      await submitResponses();
    } catch (error) {
      console.error('Error submitting responses:', error);
    }
  };

  // Función para enviar respuestas al servidor
  const submitResponses = async () => {
    try {
      console.log('Enviando respuestas al servidor:', responses);

      // Calcular la duración total de la encuesta
      const endTime = new Date();
      const durationMs = startTime ? endTime - startTime : 0;
      const durationMinutes = Math.round(durationMs / 60000 * 10) / 10; // Duración en minutos con 1 decimal

      // Calcular el sentimiento general de las respuestas
      const sentimentScores = responses.map(r => {
        // Solo analizar respuestas abiertas, no calificaciones numéricas o sí/no
        if (typeof r.value === 'string' && r.value.length > 3) {
          const sentiment = NLPService.analyzeSentiment(r.value);
          return sentiment.score;
        }
        return null;
      }).filter(score => score !== null);

      // Calcular el sentimiento promedio si hay suficientes datos
      let averageSentiment = 0;
      let sentimentLabel = 'neutral';

      if (sentimentScores.length > 0) {
        averageSentiment = sentimentScores.reduce((sum, score) => sum + score, 0) / sentimentScores.length;

        if (averageSentiment > 0.2) {
          sentimentLabel = 'positive';
        } else if (averageSentiment < -0.2) {
          sentimentLabel = 'negative';
        }
      }

      // Incluir metadatos de IA con las respuestas
      const formattedResponses = {
        responses: responses.map(r => ({
          questionId: survey.questions[r.index]._id,
          questionText: survey.questions[r.index].text,
          response: r.value,
          responseTime: r.timestamp
        })),
        metadata: {
          completionTime: durationMinutes,
          overallSentiment: sentimentLabel,
          sentimentScore: averageSentiment,
          interactionQuality: calculateInteractionQuality()
        }
      };

      // Enviar respuestas con metadatos de IA
      await ResponseService.submitResponse({
        surveyId: survey._id,
        surveyTitle: survey.title,
        respondentName: userName || 'Anónimo',
        responses: formattedResponses
      });

      // Seleccionar mensaje de despedida basado en el sentimiento general
      let farewellPool;
      if (sentimentLabel === 'positive') {
        farewellPool = ADAPTIVE_PHRASES.farewells.filter(f => f.tone === 'positive');
      } else if (sentimentLabel === 'negative') {
        farewellPool = ADAPTIVE_PHRASES.farewells.filter(f => f.tone === 'supportive');
      } else {
        farewellPool = ADAPTIVE_PHRASES.farewells.filter(f => f.tone === 'neutral');
      }

      // Si no hay mensajes del tipo adecuado, usar cualquiera
      if (farewellPool.length === 0) {
        farewellPool = ADAPTIVE_PHRASES.farewells;
      }

      const thankYouMessage = farewellPool[Math.floor(Math.random() * farewellPool.length)].text;

      // Cambiar a pantalla de agradecimiento
      setCurrentStep('thanks');

      // Hablar mensaje de agradecimiento personalizado
      await speakText(thankYouMessage);
      setShowThankYouMessage(true);

    } catch (error) {
      console.error('Error al enviar respuestas:', error);
      setErrorMessage('Hubo un problema al enviar sus respuestas. Por favor, inténtelo de nuevo más tarde.');
    }
  };

  // Calcular una puntuación de calidad de interacción basada en métricas de la encuesta
  const calculateInteractionQuality = () => {
    // Si no hay respuestas, calidad desconocida
    if (!responses || responses.length === 0) return 'unknown';

    // Verificar tiempos de respuesta
    const avgResponseTime = responses.reduce((sum, r) => sum + (r.timestamp ? new Date(r.timestamp) - new Date(startTime) : 0), 0) / responses.length;

    // Verificar cantidad de repeticiones/clarificaciones (basado en logs)
    const clarificationCount = responses.filter(r => r.required_clarification).length;

    // Calcular puntaje de 1-10
    let score = 10;

    // Penalizar tiempos de respuesta largos (más de 15 segundos por pregunta en promedio)
    if (avgResponseTime > 15000) {
      score -= Math.min(3, (avgResponseTime - 15000) / 5000);
    }

    // Penalizar por cada clarificación requerida
    score -= clarificationCount * 1.5;

    // Limitar el rango a 1-10
    score = Math.max(1, Math.min(10, score));

    // Clasificar la calidad
    if (score >= 8) return 'excellent';
    if (score >= 6) return 'good';
    if (score >= 4) return 'fair';
    return 'poor';
  };

  // Inicializar el sistema de audio al cargar la página
  useEffect(() => {
    try {
      AudioService.initSpeechSystem();
    } catch (error) {
      console.error('Error al inicializar el sistema de audio:', error);
    }
  }, []);

  // Pre-cargar las voces del navegador
  useEffect(() => {
    // Pre-cargar voces al inicio para evitar problemas
    if (window.speechSynthesis) {
      console.log('Precargando voces del sintetizador...');

      // Asegurarse de que las voces estén cargadas
      const loadVoices = () => {
        const voices = window.speechSynthesis.getVoices();
        console.log('Voces disponibles:', voices.length);

        // Mostrar voces disponibles en español
        const spanishVoices = voices.filter(v => v.lang.includes('es'));
        if (spanishVoices.length > 0) {
          console.log('Voces en español encontradas:', spanishVoices.map(v => v.name).join(', '));
        } else if (voices.length > 0) {
          console.log('No se encontraron voces en español. Voces disponibles:', voices.map(v => v.name).join(', '));
        } else {
          console.warn('No se encontraron voces disponibles');
        }
      };

      // Chrome requiere este evento
      if (window.speechSynthesis.onvoiceschanged !== undefined) {
        window.speechSynthesis.onvoiceschanged = loadVoices;
      }

      // Intentar cargar voces inmediatamente también
      loadVoices();
    }
  }, []);

  // Función para iniciar escucha automáticamente
  const startListening = async () => {
    try {
      console.log('Iniciando escucha...');

      // Verificar soporte y permisos antes de continuar
      if (!browserSupported) {
        setErrorMessage('Su navegador no es compatible con el reconocimiento de voz');
        return;
      }

      console.log('Activando el reconocimiento de voz...');
      setIsListening(true);
      setTranscript('');

      // Esperar a que termine cualquier síntesis de voz en curso
      if (isSpeaking) {
        console.log('Esperando que termine la síntesis de voz...');
        await new Promise(resolve => {
          const checkSpeaking = setInterval(() => {
            if (!isSpeaking) {
              clearInterval(checkSpeaking);
              resolve();
            }
          }, 100);
        });

        // Añadir una pausa de 1 segundo después de que termine de hablar
        // para dar tiempo al usuario de procesar lo que escuchó
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      console.log('Configurando listenForSpeech...');

      // Número máximo de intentos antes de pasar a la siguiente pregunta
      const MAX_RETRY_ATTEMPTS = 3;

      // Crear un estado local de intentos para evitar problemas con closures
      let attemptCount = 0;

      // Para guardar la instancia de reconocimiento
      let recognitionInstance = null;

      // Función para manejar el resultado del reconocimiento
      const handleRecognitionResult = (result) => {
        console.log('Reconocimiento exitoso:', result);
        setTranscript(result.transcript);
        setIsListening(false);

        if (recognitionInstance) {
          recognitionInstance.stop();
        }

        // Procesar la respuesta recibida
        processResponse(result.transcript);
      };

      // Función para manejar el fin del reconocimiento
      const handleRecognitionEnd = async () => {
        // Si no hay transcript, es porque no se detectó habla
        if (!transcript) {
          attemptCount++;
          console.log(`Intento ${attemptCount}/${MAX_RETRY_ATTEMPTS} sin respuesta detectada`);

          // Seleccionar mensaje de clarificación según el número de intento
          let clarificationMessage;
          if (attemptCount === 1) {
            clarificationMessage = 'No capté bien su respuesta. ¿Podría hablar un poco más claro?';
          } else if (attemptCount === 2) {
            clarificationMessage = 'Necesito una respuesta más clara. ¿Podría intentarlo nuevamente?';
          } else {
            clarificationMessage = 'Parece que no pude entender correctamente. ¿Podría decirlo de otra manera?';
          }

          // Hablar mensaje de clarificación
          await speakText(clarificationMessage);

          // Si aún no alcanzamos el máximo de intentos, intentar de nuevo
          if (attemptCount < MAX_RETRY_ATTEMPTS) {
            // Iniciar un nuevo reconocimiento después de una pausa
            setTimeout(() => {
              // Crear una nueva instancia de reconocimiento
              recognitionInstance = listenForSpeech(
                handleRecognitionResult,
                (error) => {
                  console.error('Error en reconocimiento de voz:', error);
                  // No mostrar errores de no-speech al usuario
                  if (!error.includes('no-speech')) {
                    setErrorMessage(error);
                  }
                },
                handleRecognitionEnd,
                0 // Sin timeout
              );
            }, 500);
          } else {
            // Si alcanzamos el máximo de intentos, pasar a la siguiente pregunta
            if (currentStep === 'name') {
              // Si estamos en el paso del nombre, usar 'Anónimo'
              setUserName('Anónimo');
              await speakText('Continuaremos sin registrar su nombre.');
              moveToQuestions();
            } else if (currentStep === 'questions') {
              // Si estamos en preguntas, guardar respuesta como 'No responde'
              saveResponse('No responde');
            }
          }
        }
      };

      // Configurar reconocimiento con callbacks
      recognitionInstance = listenForSpeech(
        handleRecognitionResult,
        (error) => {
          console.error('Error en reconocimiento de voz:', error);
          // No mostrar errores de no-speech al usuario
          if (!error.includes('no-speech')) {
            setErrorMessage(error);
          }
        },
        handleRecognitionEnd,
        0 // Sin timeout - dejamos que el reconocimiento continúe hasta que haya resultado
      );

      console.log('Configuración de reconocimiento completada, iniciando...');

    } catch (error) {
      console.error('Error al iniciar el reconocimiento de voz:', error);
      setIsListening(false);
      setErrorMessage(`Error: ${error.message}`);
    }
  };

  // Función para procesar la respuesta del usuario con NLP mejorado
  const processResponse = async (text) => {
    console.log('Procesando respuesta:', text);
    if (!text || text.trim() === '') {
      console.log('Respuesta vacía, volviendo a escuchar...');
      setTimeout(() => startListening(), 1000);
      return;
    }

    setTranscript(text);

    try {
      // Analizar el sentimiento general de la respuesta del usuario
      const sentimentAnalysis = NLPService.analyzeSentiment(text);
      console.log('Análisis de sentimiento:', sentimentAnalysis);

      if (currentStep === 'name') {
        console.log('Procesando nombre:', text);

        // Usar el servicio de NLP avanzado para extraer el nombre
        const intent = NLPService.analyzeIntent(text);

        // Si la respuesta es una pregunta, manejarla apropiadamente
        if (intent.intent === 'question') {
          const supportiveResponse = ADAPTIVE_PHRASES.clarifications
            .filter(r => r.tone === 'supportive')
            .map(r => r.text);

          const response = supportiveResponse[Math.floor(Math.random() * supportiveResponse.length)];
          await speakText('Disculpe, solo necesito saber su nombre para registrar sus respuestas. ¿Cómo se llama?');
          setTimeout(() => startListening(), 1000);
          return;
        }

        // Extraer el nombre usando el servicio NLP
        let possibleName = NLPService.extractName(text);
        console.log('Nombre extraído usando NLP:', possibleName);

        // Si no pudimos extraer un nombre válido, usar un nombre genérico
        if (!possibleName) {
          possibleName = 'Estimado participante';
        }

        // Guardar el nombre y continuar
        setUserName(possibleName);

        // Elegir respuesta basada en el sentimiento detectado
        let responsePhrase;
        if (sentimentAnalysis.sentiment === 'positive') {
          responsePhrase = '¡Encantado de conocerle, ' + possibleName + '! Vamos a comenzar con la primera pregunta.';
        } else if (sentimentAnalysis.sentiment === 'negative') {
          responsePhrase = 'Gracias, ' + possibleName + '. Vamos a iniciar la encuesta con la primera pregunta.';
        } else {
          responsePhrase = 'Muy bien, ' + possibleName + '. Comencemos con la primera pregunta.';
        }

        // Confirmar y seguir
        await speakText(responsePhrase);

        // Pasar a las preguntas con un retraso para asegurar la actualización de estado
        setTimeout(async () => {
          console.log('Pasando a las preguntas de la encuesta...');
          await moveToQuestions();
        }, 800);

        return;
      }

      if (currentStep === 'questions') {
        console.log('Procesando respuesta a pregunta:', currentQuestionIndex);
        const currentQuestion = survey.questions[currentQuestionIndex];

        // Procesar la respuesta usando el servicio NLP avanzado
        const processedResponse = await NLPService.processResponse(text, currentQuestion.type);
        console.log('Respuesta procesada con NLP:', processedResponse);

        if (processedResponse !== null) {
          // Respuesta válida
          saveResponse(processedResponse);

          // Seleccionar una respuesta adaptativa basada en el sentimiento del usuario
          let responsePool;
          if (sentimentAnalysis.sentiment === 'positive') {
            responsePool = ADAPTIVE_PHRASES.acknowledgments.filter(r => r.tone === 'positive');
          } else if (sentimentAnalysis.sentiment === 'negative') {
            responsePool = ADAPTIVE_PHRASES.acknowledgments.filter(r => r.tone === 'supportive');
          } else {
            responsePool = ADAPTIVE_PHRASES.acknowledgments.filter(r => r.tone === 'neutral');
          }

          // Si no hay respuestas del tipo adecuado, usar cualquiera
          if (responsePool.length === 0) {
            responsePool = ADAPTIVE_PHRASES.acknowledgments;
          }

          // Seleccionar respuesta adaptativa
          const acknowledgment = responsePool[Math.floor(Math.random() * responsePool.length)].text;
          await speakText(acknowledgment);

          // Transición a siguiente pregunta o finalizar
          if (currentQuestionIndex < survey.questions.length - 1) {
            // Seleccionar transición según sentimiento
            let transitionPool;
            if (sentimentAnalysis.sentiment === 'positive') {
              transitionPool = ADAPTIVE_PHRASES.transitions.filter(r => r.tone === 'positive');
            } else if (sentimentAnalysis.sentiment === 'negative') {
              transitionPool = ADAPTIVE_PHRASES.transitions.filter(r => r.tone === 'supportive');
            } else {
              transitionPool = ADAPTIVE_PHRASES.transitions.filter(r => r.tone === 'neutral');
            }

            if (transitionPool.length === 0) {
              transitionPool = ADAPTIVE_PHRASES.transitions;
            }

            const transition = transitionPool[Math.floor(Math.random() * transitionPool.length)].text;
            await speakText(transition);

            // Incrementar el índice y pasar a la siguiente pregunta
            setCurrentQuestionIndex(prevIndex => prevIndex + 1);

            // Esperar a que se actualice el estado antes de continuar
            console.log('Avanzando a la siguiente pregunta...');

            // Usar setTimeout para asegurar que el cambio de estado se complete
            setTimeout(async () => {
              console.log('Ejecutando speakCurrentQuestion desde setTimeout');
              await speakCurrentQuestion();
            }, 1000);
          } else {
            // Finalizar encuesta con un mensaje adaptativo basado en el sentimiento general
            console.log('Terminando encuesta, enviando respuestas...');
            await submitResponses();
          }
        } else {
          // Respuesta inválida, elegir clarificación basada en sentimiento
          console.log('Respuesta inválida, pidiendo aclaración...');

          let clarificationPool;
          if (sentimentAnalysis.sentiment === 'negative') {
            // Si el usuario parece frustrado, usar tono de apoyo
            clarificationPool = ADAPTIVE_PHRASES.clarifications.filter(r => r.tone === 'supportive');
          } else {
            clarificationPool = ADAPTIVE_PHRASES.clarifications.filter(r => r.tone === 'neutral');
          }

          if (clarificationPool.length === 0) {
            clarificationPool = ADAPTIVE_PHRASES.clarifications;
          }

          const clarification = clarificationPool[Math.floor(Math.random() * clarificationPool.length)].text;
          await speakText(clarification);

          // Usar setTimeout para evitar problemas con promesas anidadas
          setTimeout(async () => {
            console.log('Reiniciando escucha después de aclaración');
            await startListening();
          }, 800);
        }
      }
    } catch (error) {
      console.error('Error durante el procesamiento de la respuesta:', error);
      // Intentar recuperarse del error
      setTimeout(async () => {
        if (currentStep === 'name') {
          await speakText('Lo siento, hubo un problema. Intentemos de nuevo. ¿Podría decirme su nombre?');
          setTimeout(() => startListening(), 1000);
        } else if (currentStep === 'questions') {
          await speakCurrentQuestion();
        }
      }, 1500);
    }
  };

  // Función para extraer un nombre de texto
  const extractName = (text) => {
    console.log('Extrayendo nombre de:', text);

    // Si el texto está vacío o no es una cadena
    if (!text || typeof text !== 'string') {
      console.log('Texto vacío o no es cadena, usando nombre genérico');
      return 'Estimado participante';
    }

    // Eliminar signos de puntuación y normalizar texto
    const normalizedText = text.replace(/[.,;:¡!¿?]/g, '').trim();

    // Frases comunes al indicar un nombre
    const namePhrases = [
      'me llamo', 'mi nombre es', 'soy', 'yo soy', 
      'puedes llamarme', 'puede llamarme', 'llámame', 'llamame'
    ];

    // Buscar frases de presentación
    let cleanedText = normalizedText;
    for (const phrase of namePhrases) {
      if (normalizedText.toLowerCase().includes(phrase)) {
        // Extraer texto después de la frase de presentación
        const parts = normalizedText.toLowerCase().split(phrase);
        if (parts.length > 1 && parts[1].trim()) {
          cleanedText = parts[1].trim();
          console.log(`Frase de presentación '${phrase}' encontrada, texto limpio: '${cleanedText}'`);
          break;
        }
      }
    }

    // Palabras comunes para ignorar al final
    const commonWords = ['gracias', 'por favor', 'aquí', 'señor', 'señora', 'mucho gusto'];
    for (const word of commonWords) {
      if (cleanedText.toLowerCase().endsWith(word)) {
        cleanedText = cleanedText.slice(0, -(word.length)).trim();
      }
    }

    // Si el texto limpio está vacío, usar el original
    if (!cleanedText) {
      cleanedText = normalizedText;
    }

    // Obtener las palabras del texto
    const words = cleanedText.split(/\s+/);

    // Si solo hay una palabra, asumimos que es el nombre
    if (words.length === 1) {
      const name = words[0];
      console.log('Nombre extraído (una palabra):', name);
      // Capitalizar primera letra
      return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    }

    // Si hay más palabras, tomar hasta 3 palabras como nombre completo
    const nameWords = words.slice(0, Math.min(3, words.length));
    let extractedName = nameWords.join(' ');

    // Capitalizar cada palabra
    extractedName = extractedName.split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(' ');

    console.log('Nombre extraído (múltiples palabras):', extractedName);
    return extractedName;
  };

  // Función para guardar respuesta y pasar a la siguiente pregunta
  const saveResponse = (value) => {
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = {
      value,
      timestamp: new Date().toISOString(),
      index: currentQuestionIndex
    };
    setResponses(updatedResponses);

    // Verificar si hay más preguntas
    if (currentQuestionIndex < survey.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Fin de la encuesta
      setCurrentStep('thanks');
    }
  };

  // Manejar clic en botón de micrófono
  const handleMicrophoneClick = () => {
    if (isListening) {
      setIsListening(false);
    } else {
      startListening();
    }
  };

  // Manejar clic en botones de opciones 
  const handleOptionClick = (value) => {
    if (isListening) return;

    processResponse(value);
  };

  // Solicitar permisos de micrófono
  const requestMicrophonePermission = async () => {
    try {
      const permission = await checkMicrophonePermission();
      setMicrophonePermission(permission);

      if (permission) {
        // Iniciar la encuesta si se conceden los permisos
        setCurrentStep('welcome');
      } else {
        setErrorMessage('No se pudo obtener permiso para el micrófono. Por favor, permita el acceso en la configuración de su navegador.');
      }
    } catch (error) {
      console.error('Error al solicitar permisos de micrófono:', error);
      setErrorMessage('Error al solicitar permisos de micrófono.');
    }
  };

  // Función centralizada para solicitar síntesis de voz
  const speakText = async (text) => {
    if (!text) return Promise.resolve();
    console.log('Solicitando síntesis de voz para:', text);

    // Evitar interrupción de síntesis en curso y garantizar una experiencia fluida
    return new Promise((resolve) => {
      setVoiceText(text);
      setIsSpeaking(true);

      // Usar AudioService para hablar
      AudioService.speakText(
        text, 
        // onStarted
        () => {
          console.log('Síntesis de voz iniciada para:', text);
        },
        // onEnded
        () => {
          console.log('Síntesis de voz completada para:', text);
          setIsSpeaking(false);
          if (speakEndCallback) {
            speakEndCallback();
            setSpeakEndCallback(null);
          }
          resolve();
        },
        // onError
        (error) => {
          console.error('Error en síntesis de voz:', error);
          setIsSpeaking(false);
          setErrorMessage(`Error en síntesis: ${error}`);
          resolve();
        }
      );
    });
  };

  useEffect(() => {
    setStartTime(new Date());
  }, []);

  // Función para repetir la pregunta actual
  const repeatQuestion = async () => {
    try {
      if (currentStep === 'questions' && survey && survey.questions) {
        // Detener cualquier síntesis o escucha en curso
        if (isListening) {
          // Detener escucha si está activa
          setIsListening(false);
        }

        const currentQuestion = survey.questions[currentQuestionIndex];
        if (currentQuestion) {
          // Construir el mensaje de la pregunta
          const questionText = `La pregunta es: ${currentQuestion.text}`;

          // Hablar la pregunta
          await speakText(questionText);

          // Esperar un momento antes de comenzar a escuchar
          await new Promise(resolve => setTimeout(resolve, 1000));

          // Iniciar escucha para la respuesta
          startListening();
        }
      }
    } catch (error) {
      console.error('Error al repetir la pregunta:', error);
      setErrorMessage('Ocurrió un error al repetir la pregunta.');
    }
  };

  // Renderizar pantalla de carga
  if (loading) {
    return <LoadingSpinner />;
  }

  // Renderizar mensaje de error
  if (errorMessage) {
    return <ErrorMessage message={errorMessage} />;
  }

  // Si no hay encuesta
  if (!survey) {
    return <ErrorMessage message="No se encontró la encuesta solicitada." />;
  }

  // Obtener la pregunta actual
  const currentQuestion = survey.questions[currentQuestionIndex];

  return (
    <div className="max-w-3xl mx-auto p-4">
      {/* Encabezado de la encuesta */}
      <div className="mb-8 text-center">
        <h1 className="text-3xl font-bold mb-2">{survey.title}</h1>
        <p className="text-gray-600">{survey.description}</p>
        {currentQuestionIndex === 0 && survey.welcomeMessage && (
          <div className="mt-4 p-4 bg-blue-50 rounded-lg">
            <p>{survey.welcomeMessage}</p>
          </div>
        )}
      </div>

      {/* Progreso */}
      <div className="mb-6">
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className="bg-blue-600 h-2.5 rounded-full" 
            style={{ width: `${((currentQuestionIndex + 1) / survey.questions.length) * 100}%` }}
          ></div>
        </div>
        <p className="text-right text-sm mt-1">
          Pregunta {currentQuestionIndex + 1} de {survey.questions.length}
        </p>
      </div>

      {/* Pantalla de confirmación */}
      {showThankYouMessage ? (
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold mb-4">¡Gracias por completar la encuesta!</h2>
          <p className="mb-4">Sus respuestas han sido registradas correctamente.</p>
          <button
            onClick={() => navigate('/surveys')}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Volver al inicio
          </button>
        </div>
      ) : (
        <>
          {/* Pregunta actual */}
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-bold mb-4">{currentQuestion.text}</h2>

            {/* Opciones para preguntas de tipo opción */}
            {currentQuestion.type === 'multiple_choice' && currentQuestion.options && (
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">Opciones:</p>
                <ul className="list-disc pl-5">
                  {currentQuestion.options.map((option, idx) => (
                    <li key={idx} className="mb-1">{option}</li>
                  ))}
                </ul>
              </div>
            )}

            {/* Área de respuesta */}
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tu respuesta:
              </label>
              <textarea
                value={currentResponse}
                onChange={(e) => setCurrentResponse(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Escribe o habla tu respuesta..."
              ></textarea>

              {/* Transcripción en tiempo real */}
              {isListening && (
                <div className="mt-2 p-2 bg-yellow-50 rounded text-sm">
                  <p className="font-medium">Escuchando...</p>
                  <p>{transcript || "..."}</p>
                </div>
              )}
            </div>

            {/* Botón de reconocimiento de voz */}
            {browserSupported && (
              <button
                onClick={startListening}
                className={`mt-3 px-4 py-2 rounded-full flex items-center ${
                  isListening ? 'bg-red-500 text-white' : 'bg-blue-500 text-white'
                }`}
              >
                <span className="material-icons mr-1">
                  {isListening ? 'mic_off' : 'mic'}
                </span>
                {isListening ? 'Detener' : 'Hablar'}
              </button>
            )}
          </div>

          {/* Botones de navegación */}
          <div className="flex justify-between">
            <button
              onClick={repeatQuestion}
              disabled={currentQuestionIndex === 0}
              className={`px-4 py-2 rounded ${
                currentQuestionIndex === 0
                  ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
            >
              Anterior
            </button>

            <button
              onClick={() => processResponse(currentResponse)}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              {currentQuestionIndex < survey.questions.length - 1 ? 'Siguiente' : 'Finalizar'}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TakeSurvey;
