import React, { useState } from 'react';

const VoiceTest = () => {
  const [message, setMessage] = useState('');

  // Función de prueba directa para la síntesis de voz
  const testSpeech = () => {
    setMessage('Probando síntesis de voz...');

    if (!window.speechSynthesis) {
      setMessage('Error: Tu navegador no soporta la síntesis de voz');
      return;
    }

    // Cancelar cualquier síntesis anterior
    window.speechSynthesis.cancel();

    // Crear un nuevo mensaje
    const utterance = new SpeechSynthesisUtterance('Hola, esta es una prueba de voz. Si puedes escuchar esto, la síntesis de voz está funcionando correctamente.');

    // Configurar idioma
    utterance.lang = 'es-ES';
    utterance.rate = 1;
    utterance.pitch = 1;

    // Eventos
    utterance.onstart = () => {
      setMessage('Reproduciendo audio...');
    };

    utterance.onend = () => {
      setMessage('Síntesis de voz completada con éxito');
    };

    utterance.onerror = (event) => {
      setMessage(`Error en síntesis de voz: ${event.error}`);
    };

    // Reproducir
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="container mx-auto p-8 max-w-2xl">
      <h1 className="text-2xl font-bold mb-6">Prueba de Síntesis de Voz</h1>

      <div className="card p-6 mb-8">
        <p className="mb-4">
          Haz clic en el botón para probar la síntesis de voz. Si escuchas una voz después de hacer 
          clic, significa que tu navegador admite Text-to-Speech correctamente.
        </p>

        <button 
          onClick={testSpeech}
          className="btn btn-primary mb-4"
        >
          Probar Síntesis de Voz
        </button>

        {message && (
          <div className="mt-4 p-3 bg-gray-100 rounded">
            <p>{message}</p>
          </div>
        )}
      </div>

      <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6">
        <h3 className="font-bold text-yellow-800">Importante</h3>
        <p className="text-yellow-700">
          Muchos navegadores requieren interacción del usuario (como un clic) antes de permitir
          la reproducción de audio. Por esta razón, la primera vez que abres una encuesta,
          es posible que debas hacer clic en algún elemento antes de que funcione la síntesis de voz.
        </p>
      </div>

      <h2 className="text-xl font-semibold mb-4">Solución de problemas:</h2>
      <ul className="list-disc pl-6 space-y-2">
        <li>Asegúrate de que tu navegador sea compatible (Chrome, Edge, Safari)</li>
        <li>Verifica que el volumen de tu dispositivo esté activado</li>
        <li>Algunos navegadores requieren permisos de audio - concede cualquier permiso solicitado</li>
        <li>Si usas Chrome, verifica que no esté silenciando la pestaña</li>
      </ul>
    </div>
  );
};

export default VoiceTest;
