import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { SurveyService, ResponseService } from '../services/apiService';

// Componentes
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';
import audioService from '../services/audioService';
// Importar servicio de procesamiento de lenguaje natural
import * as nlpService from '../services/nlpService';

const TakeSurvey = () => {
  const { surveyId } = useParams();
  const navigate = useNavigate();

  // Referencias para controlar el flujo de la conversación
  const conversationActive = useRef(false);
  const speakTimeoutRef = useRef(null);

  // Estados
  const [survey, setSurvey] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [currentResponse, setCurrentResponse] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [conversationState, setConversationState] = useState('idle'); // idle, speaking, listening, processing
  const [conversationMessage, setConversationMessage] = useState('');
  const [micPermission, setMicPermission] = useState('unknown'); // 'unknown', 'granted', 'denied'
  const [recognizedText, setRecognizedText] = useState('');
  const [status, setStatus] = useState('');
  const [showPermissionDialog, setShowPermissionDialog] = useState(false);

  // Verificar permiso del micrófono
  const requestMicrophonePermission = async () => {
    setConversationState('requesting_permission');
    setConversationMessage('Solicitando permiso para el micrófono...');

    try {
      const permissionGranted = await audioService.requestMicrophonePermission();

      if (permissionGranted) {
        setMicPermission('granted');
        setShowPermissionDialog(false);
        setConversationMessage('Permiso concedido. Iniciando encuesta por voz...');

        // Pequeño retraso para iniciar la conversación
        setTimeout(() => {
          startConversation();
        }, 1000);
      } else {
        setMicPermission('denied');
        setConversationState('permission_denied');
        setConversationMessage('Se necesita acceso al micrófono para usar la función de voz');
      }
    } catch (error) {
      console.error('Error al solicitar permiso:', error);
      setMicPermission('denied');
      setConversationState('error');
      setConversationMessage('Error al solicitar permiso del micrófono');
    }
  };

  // Cargar la encuesta
  useEffect(() => {
    const fetchSurvey = async () => {
      try {
        const data = await SurveyService.getPublicSurvey(surveyId);
        console.log('Encuesta cargada:', data);
        setSurvey(data);
        // Inicializar respuestas vacías
        setResponses(new Array(data.questions.length).fill(''));
      } catch (error) {
        console.error('Error al cargar la encuesta:', error);
        setError('No se pudo cargar la encuesta. Verifique que el ID sea correcto y que la encuesta esté activa.');
      } finally {
        setLoading(false);
      }
    };

    fetchSurvey();

    // Inicializar sistema de voz
    audioService.initSpeechSystem();

    // Configurar el callback para cambios en el permiso del micrófono
    audioService.onPermissionChange((granted, errorMsg) => {
      setMicPermission(granted ? 'granted' : 'denied');
      if (!granted && errorMsg) {
        setConversationMessage(`Error de permiso: ${errorMsg}`);
        setConversationState('permission_denied');
      }
    });

    return () => {
      // Detener cualquier síntesis y reconocimiento al desmontar
      if (speakTimeoutRef.current) {
        clearTimeout(speakTimeoutRef.current);
      }
      audioService.stop();
      conversationActive.current = false;

      if (window.speechSynthesis) {
        window.speechSynthesis.cancel();
      }
    };
  }, [surveyId]);

  // Iniciar la conversación una vez que la encuesta esté cargada
  useEffect(() => {
    if (survey && voiceEnabled && !conversationActive.current) {
      // Primero verificar permiso de micrófono
      if (micPermission === 'granted') {
        startConversation();
      } else if (micPermission === 'unknown') {
        // Mostrar diálogo de permiso
        setShowPermissionDialog(true);
      }
    }
  }, [survey, voiceEnabled, micPermission]);

  // Función para iniciar la conversación
  const startConversation = () => {
    if (!voiceEnabled || !survey) return;

    conversationActive.current = true;

    // Dar un mensaje de bienvenida
    if (survey.welcomeMessage) {
      speakText(survey.welcomeMessage, () => {
        // Después de la bienvenida, preguntar la primera pregunta
        speakCurrentQuestion();
      });
    } else {
      // Si no hay mensaje de bienvenida, ir directamente a la primera pregunta
      speakCurrentQuestion();
    }
  };

  // Función para hablar el texto usando el servicio de audio con pausas naturales
  const speakText = (text, onEndCallback) => {
    if (!voiceEnabled) {
      if (onEndCallback) onEndCallback();
      return;
    }

    // Cancela cualquier síntesis en curso y limpia los timeouts anteriores
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }

    if (speakTimeoutRef.current) {
      clearTimeout(speakTimeoutRef.current);
      speakTimeoutRef.current = null;
    }

    // Pausa breve antes de comenzar a hablar para evitar interrupciones
    speakTimeoutRef.current = setTimeout(() => {
      setConversationState('speaking');
      setConversationMessage('Hablando: ' + text.substring(0, 30) + (text.length > 30 ? '...' : ''));

      // Calcular un tiempo estimado basado en la longitud del texto (aproximadamente 80ms por carácter)
      const estimatedSpeakTime = Math.max(3000, text.length * 80);

      audioService.speakText(
        text,
        () => {
          console.log('Hablando:', text);
        },
        () => {
          console.log('Terminó de hablar');
          setConversationState('idle');

          // Esperar un tiempo más largo después de hablar para una conversación más natural
          if (onEndCallback) {
            speakTimeoutRef.current = setTimeout(onEndCallback, 1000);
          }
        },
        (error) => {
          console.error('Error al hablar:', error);
          setConversationState('idle');

          // En caso de error, esperar un poco antes de continuar
          if (onEndCallback) {
            speakTimeoutRef.current = setTimeout(onEndCallback, 1000);
          }
        }
      );
    }, 500); // Esperar 500ms antes de iniciar una nueva síntesis
  };

  // Función para hablar la pregunta actual
  const speakCurrentQuestion = () => {
    if (!survey || !voiceEnabled) return;

    const currentQuestion = survey.questions[currentQuestionIndex];
    if (!currentQuestion) return;

    let questionText = `Pregunta ${currentQuestionIndex + 1}: ${currentQuestion.text}`;

    // Agregar información sobre opciones si es pregunta de opción múltiple
    if (currentQuestion.type === 'multiple_choice' && currentQuestion.options) {
      questionText += '. Las opciones son: ';
      questionText += currentQuestion.options.map((option, idx) => 
        `Opción ${idx + 1}: ${option}`
      ).join(', ');
    }

    speakText(questionText, () => {
      // Después de hablar la pregunta, iniciar el reconocimiento de voz
      startListening();
    });
  };

  // Función para iniciar la escucha de la respuesta del usuario
  const startListening = async () => {
    if (!voiceEnabled) return;

    if (!audioService.isSupportedByBrowser()) {
      setError('Tu navegador no soporta reconocimiento de voz');
      return;
    }

    if (micPermission !== 'granted') {
      await requestMicrophonePermission();
      return;
    }

    audioService.init('es-ES');

    setRecognizedText('');

    audioService.onResult((transcript, isFinal) => {
      setCurrentResponse(transcript);
      setStatus(isFinal ? 'Procesando...' : 'Escuchando...');
    });

    audioService.onEnd((finalTranscript) => {
      setIsListening(false);
      setConversationState('processing');
      setConversationMessage('Procesando respuesta...');

      // Guardar la respuesta y continuar la conversación
      handleVoiceResponse(finalTranscript);
    });

    audioService.onError((errorMessage) => {
      console.error('Error de reconocimiento:', errorMessage);
      setError(`Error de reconocimiento: ${errorMessage}`);
      setIsListening(false);
      setConversationState('idle');

      // Reintentar la escucha después de un error
      speakTimeoutRef.current = setTimeout(() => {
        speakText('No pude entender tu respuesta. Por favor, inténtalo de nuevo.', () => {
          startListening();
        });
      }, 1000);
    });

    try {
      const started = await audioService.start();
      if (started) {
        setIsListening(true);
        setConversationState('listening');
        setConversationMessage('Escuchando...');
      } else {
        setError('No se pudo iniciar el reconocimiento de voz');
        setConversationState('idle');
      }
    } catch (error) {
      setError(`Error al iniciar reconocimiento: ${error.message || 'Error desconocido'}`);
      setConversationState('idle');
    }
  };

  // Manejar la respuesta por voz
  const handleVoiceResponse = (response) => {
    if (!response || response.trim() === '') {
      // Si no hay respuesta, pedir que repita
      speakText('No he escuchado ninguna respuesta. Por favor, intenta de nuevo.', () => {
        startListening();
      });
      return;
    }

    // Guardar la respuesta actual
    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = response;
    setResponses(updatedResponses);

    // Dar feedback al usuario
    speakText(`He registrado tu respuesta: ${response}.`, () => {
      // Pasar a la siguiente pregunta o finalizar
      if (currentQuestionIndex < survey.questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setCurrentResponse('');
        // Hablar la siguiente pregunta después de un breve pausa
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 1000);
      } else {
        // Si es la última pregunta, mostrar confirmación
        setShowConfirmation(true);
        speakText('Hemos terminado todas las preguntas. Ahora te mostraré un resumen de tus respuestas para que las confirmes.', () => {
          speakConfirmationSummary();
        });
      }
    });
  };

  // Leer el resumen de confirmación
  const speakConfirmationSummary = () => {
    if (!voiceEnabled || !showConfirmation) return;

    let summaryText = 'Resumen de tus respuestas: ';

    survey.questions.forEach((question, index) => {
      summaryText += `Pregunta ${index + 1}: ${question.text}. Tu respuesta: ${responses[index] || "Sin respuesta"}. `;
    });

    summaryText += '¿Deseas enviar estas respuestas o volver para revisar alguna?';

    speakText(summaryText, () => {
      // Escuchar confirmación del usuario
      listenForConfirmation();
    });
  };

  // Escuchar la confirmación del usuario
  const listenForConfirmation = () => {
    if (!voiceEnabled) return;

    audioService.init('es-ES');

    audioService.onResult((transcript, isFinal) => {
      // Mostrar lo que está diciendo el usuario
      setCurrentResponse(transcript);
    });

    audioService.onEnd((finalTranscript) => {
      setIsListening(false);

      // Analizar respuesta para determinar si es confirmación o no
      const lowerResponse = finalTranscript.toLowerCase();
      if (
        lowerResponse.includes('sí') || 
        lowerResponse.includes('si') || 
        lowerResponse.includes('confirmar') || 
        lowerResponse.includes('enviar') ||
        lowerResponse.includes('aceptar') ||
        lowerResponse.includes('está bien')
      ) {
        // Confirmado - enviar respuestas
        speakText('Perfecto, enviando tus respuestas.', () => {
          handleSubmit();
        });
      } else if (
        lowerResponse.includes('no') || 
        lowerResponse.includes('volver') || 
        lowerResponse.includes('revisar') ||
        lowerResponse.includes('regresar')
      ) {
        // Volver a revisar
        setShowConfirmation(false);
        speakText('De acuerdo, volvamos a revisar las preguntas. Puedes navegar entre ellas usando los botones de anterior y siguiente.', null);
      } else {
        // Respuesta no clara
        speakText('No he entendido si deseas confirmar o revisar. Por favor, dime "confirmar" para enviar las respuestas o "revisar" para volver a las preguntas.', () => {
          listenForConfirmation();
        });
      }
    });

    audioService.onError(() => {
      setIsListening(false);
      speakText('Hubo un error al escuchar tu confirmación. Por favor, usa los botones en pantalla para confirmar o volver.', null);
    });

    audioService.start();
    setIsListening(true);
    setConversationState('listening');
    setConversationMessage('Esperando confirmación...');
  };

  // Manejar el cambio de respuesta manual
  const handleResponseChange = (e) => {
    setCurrentResponse(e.target.value);
  };

  // Guardar la respuesta actual y avanzar a la siguiente pregunta manualmente
  const handleNextQuestion = () => {
    // Cancelar cualquier síntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();

    const updatedResponses = [...responses];
    updatedResponses[currentQuestionIndex] = currentResponse;
    setResponses(updatedResponses);

    if (currentQuestionIndex < survey.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setCurrentResponse('');

      // Si está habilitada la voz, hablar la siguiente pregunta
      if (voiceEnabled) {
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 500);
      }
    } else {
      setShowConfirmation(true);
      if (voiceEnabled) {
        speakText('Hemos terminado todas las preguntas. Ahora te mostraré un resumen de tus respuestas para que las confirmes.', () => {
          speakConfirmationSummary();
        });
      }
    }
  };

  // Volver a la pregunta anterior manualmente
  const handlePreviousQuestion = () => {
    // Cancelar cualquier síntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();

    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
      setCurrentResponse(responses[currentQuestionIndex - 1]);

      // Si está habilitada la voz, hablar la pregunta anterior
      if (voiceEnabled) {
        speakTimeoutRef.current = setTimeout(() => {
          speakCurrentQuestion();
        }, 500);
      }
    }
  };

  // Enviar todas las respuestas
  const handleSubmit = async () => {
    setIsSubmitting(true);

    try {
      // Preparar objeto de respuesta
      const responseData = {
        surveyId: survey._id,
        responses: survey.questions.map((question, index) => ({
          questionId: question._id,
          questionText: question.text,
          responseText: responses[index] || '',
          questionType: question.type
        }))
      };

      // Enviar respuesta a la API
      await ResponseService.submitResponse(responseData);

      // Mensaje de despedida
      if (voiceEnabled) {
        speakText(survey.farewell || '¡Gracias por completar la encuesta!', () => {
          // Redirigir a página de agradecimiento
          navigate(`/thank-you`, { 
            state: { 
              message: survey.farewell || '¡Gracias por completar la encuesta!' 
            } 
          });
        });
      } else {
        // Redirigir a página de agradecimiento sin hablar
        navigate(`/thank-you`, { 
          state: { 
            message: survey.farewell || '¡Gracias por completar la encuesta!' 
          } 
        });
      }
    } catch (error) {
      console.error('Error al enviar respuestas:', error);
      setError('Ocurrió un error al enviar tus respuestas. Por favor, intenta nuevamente.');
      setIsSubmitting(false);

      if (voiceEnabled) {
        speakText('Ha ocurrido un error al enviar tus respuestas. Por favor, intenta nuevamente.', null);
      }
    }
  };

  // Iniciar reconocimiento de voz manual
  const startVoiceRecognition = () => {
    if (isListening) return;

    // Cancelar cualquier síntesis en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }

    startListening();
  };

  // Detener reconocimiento de voz manual
  const stopVoiceRecognition = () => {
    audioService.stop();
    setIsListening(false);
    setConversationState('idle');
  };

  // Alternar el uso de voz
  const toggleVoice = () => {
    // Cancelar cualquier síntesis o reconocimiento en curso
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    audioService.stop();

    const newVoiceEnabled = !voiceEnabled;
    setVoiceEnabled(newVoiceEnabled);

    if (newVoiceEnabled && !conversationActive.current) {
      // Si se activa la voz, iniciar conversación
      conversationActive.current = false; // Reset para que startConversation funcione
      startConversation();
    }
  };

  // Renderizar pantalla de carga
  if (loading) {
    return <LoadingSpinner />;
  }

  // Renderizar mensaje de error
  if (error) {
    return <ErrorMessage message={error} />;
  }

  // Si no hay encuesta
  if (!survey) {
    return <ErrorMessage message="No se encontró la encuesta solicitada." />;
  }

  // Obtener la pregunta actual
  const currentQuestion = survey.questions[currentQuestionIndex];

  return (
    <div className="max-w-3xl mx-auto p-4">
      {/* Control de voz */}
      <div className="flex justify-end mb-2">
        <button 
          onClick={toggleVoice} 
          className={`px-3 py-1 rounded text-sm ${voiceEnabled ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-700'}`}
        >
          {voiceEnabled ? '🔊 Voz Activada' : '🔇 Voz Desactivada'}
        </button>
      </div>

      {/* Diálogo de permisos del micrófono */}
      {showPermissionDialog && micPermission === 'unknown' && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 max-w-md mx-auto">
            <h2 className="text-xl font-bold mb-4 text-center">Permiso para usar el micrófono</h2>
            <p className="mb-4">
              Esta encuesta utiliza reconocimiento de voz para una experiencia más natural. 
              Para continuar, necesitamos tu permiso para acceder al micrófono.
            </p>
            <div className="flex flex-col gap-3 mt-6">
              <button
                onClick={requestMicrophonePermission}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex items-center justify-center gap-2"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M7 4a3 3 0 016 0v4a3 3 0 11-6 0V4zm4 10.93A7.001 7.001 0 0017 8a1 1 0 10-2 0A5 5 0 015 8a1 1 0 00-2 0 7.001 7.001 0 006 6.93V17H6a1 1 0 100 2h8a1 1 0 100-2h-3v-2.07z" clipRule="evenodd" />
                </svg>
                Permitir micrófono
              </button>
              <button
                onClick={() => {
                  setShowPermissionDialog(false);
                  setVoiceEnabled(false);
                  setMicPermission('denied');
                }}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
              >
                Continuar sin voz
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Estado de la conversación */}
      {voiceEnabled && conversationState !== 'idle' && (
        <div className={`mb-4 p-3 rounded-lg shadow-sm text-center ${
          conversationState === 'speaking' ? 'bg-blue-100 text-blue-700' : 
          conversationState === 'listening' ? 'bg-green-100 text-green-700' : 
          conversationState === 'requesting_permission' ? 'bg-purple-100 text-purple-700' :
          conversationState === 'permission_denied' ? 'bg-red-100 text-red-700' :
          'bg-yellow-100 text-yellow-700'
        }`}>
          <p className="font-semibold flex items-center justify-center gap-2">
            {conversationState === 'speaking' && (
              <span className="inline-flex justify-center items-center">
                <span className="h-2 w-2 bg-blue-600 rounded-full animate-ping mr-1"></span>
                <span className="h-2 w-2 bg-blue-600 rounded-full animate-ping mx-1"></span>
                <span className="h-2 w-2 bg-blue-600 rounded-full animate-ping ml-1"></span>
              </span>
            )}
            {conversationState === 'listening' && (
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 animate-pulse" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M7 4a3 3 0 016 0v4a3 3 0 11-6 0V4zm4 10.93A7.001 7.001 0 0017 8a1 1 0 10-2 0A5 5 0 015 8a1 1 0 00-2 0 7.001 7.001 0 006 6.93V17H6a1 1 0 100 2h8a1 1 0 100-2h-3v-2.07z" clipRule="evenodd" />
              </svg>
            )}
            {conversationMessage}
          </p>
          {isListening && (
            <p className="text-sm mt-2 text-green-700">Escuchándote... habla claramente cuando escuches el tono.</p>
          )}
        </div>
      )}

      {/* Mensaje de error de permisos */}
      {micPermission === 'denied' && voiceEnabled && (
        <div className="mb-4 p-3 rounded-lg bg-red-50 border border-red-200">
          <p className="text-red-700 flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
            Se necesita acceso al micrófono para usar la funcionalidad de voz.
          </p>
          <div className="mt-2 flex justify-end">
            <button 
              onClick={requestMicrophonePermission}
              className="text-sm px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Reintentar
            </button>
            <button 
              onClick={() => setVoiceEnabled(false)}
              className="text-sm px-3 py-1 bg-gray-200 text-gray-700 rounded hover:bg-gray-300 ml-2"
            >
              Continuar sin voz
            </button>
          </div>
        </div>
      )}

      {/* Encabezado de la encuesta */}
      <div className="mb-8 text-center">
        <h1 className="text-3xl font-bold mb-2">{survey.title}</h1>
        <p className="text-gray-600">{survey.description}</p>
        {currentQuestionIndex === 0 && survey.welcomeMessage && (
          <div className="mt-4 p-4 bg-blue-50 rounded-lg">
            <p>{survey.welcomeMessage}</p>
          </div>
        )}
      </div>

      {/* Progreso */}
      <div className="mb-6">
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className="bg-blue-600 h-2.5 rounded-full" 
            style={{ width: `${((currentQuestionIndex + 1) / survey.questions.length) * 100}%` }}
          ></div>
        </div>
        <p className="text-right text-sm mt-1">
          Pregunta {currentQuestionIndex + 1} de {survey.questions.length}
        </p>
      </div>

      {/* Pantalla de confirmación */}
      {showConfirmation ? (
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold mb-4">Confirmar envío</h2>
          <p className="mb-4">¿Estás seguro de que deseas enviar tus respuestas?</p>

          <div className="mb-6">
            <h3 className="font-bold mb-2">Resumen de respuestas:</h3>
            {survey.questions.map((question, index) => (
              <div key={question._id} className="mb-3">
                <p className="font-medium">{index + 1}. {question.text}</p>
                <p className="text-gray-700 pl-4">{responses[index] || "(Sin respuesta)"}</p>
              </div>
            ))}
          </div>

          {isListening && (
            <div className="bg-green-100 p-3 rounded mb-4 animate-pulse">
              <p className="text-center text-green-800">
                🎤 Di "confirmar" para enviar o "revisar" para volver
              </p>
              {currentResponse && (
                <p className="text-sm italic text-center mt-1">"{currentResponse}"</p>
              )}
            </div>
          )}

          <div className="flex justify-between">
            <button
              onClick={() => {
                setShowConfirmation(false);
                if (voiceEnabled) {
                  speakText('Volvamos a revisar las preguntas.', null);
                }
              }}
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
              disabled={isSubmitting}
            >
              Volver
            </button>
            <button
              onClick={handleSubmit}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Enviando...' : 'Confirmar envío'}
            </button>
          </div>
        </div>      ) : (
        <>
          {/* Pregunta actual */}
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-bold mb-4">{currentQuestion.text}</h2>

            {/* Opciones para preguntas de tipo opción */}
            {currentQuestion.type === 'multiple_choice' && currentQuestion.options && (
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">Opciones:</p>
                <ul className="list-disc pl-5">
                  {currentQuestion.options.map((option, idx) => (
                    <li key={idx} className="mb-1">{option}</li>
                  ))}
                </ul>
              </div>
            )}

            {/* Área de respuesta */}
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tu respuesta:
              </label>
              <textarea
                value={currentResponse}
                onChange={handleResponseChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Escribe tu respuesta..."
              ></textarea>

              {/* Controles de reconocimiento de voz */}
              <div className="flex items-center mt-2">
                {voiceEnabled && micPermission === 'granted' && !isListening ? (
                  <button
                    onClick={startVoiceRecognition}
                    className="flex items-center px-3 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 shadow-sm"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M7 4a3 3 0 016 0v4a3 3 0 11-6 0V4zm4 10.93A7.001 7.001 0 0017 8a1 1 0 10-2 0A5 5 0 015 8a1 1 0 00-2 0 7.001 7.001 0 006 6.93V17H6a1 1 0 100 2h8a1 1 0 100-2h-3v-2.07z" clipRule="evenodd" />
                    </svg>
                    Responder por voz
                  </button>
                ) : voiceEnabled && micPermission === 'granted' && isListening ? (
                  <button
                    onClick={stopVoiceRecognition}
                    className="flex items-center px-3 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 shadow-sm animate-pulse"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8 7a1 1 0 00-1 1v4a1 1 0 001 1h4a1 1 0 001-1V8a1 1 0 00-1-1H8z" clipRule="evenodd" />
                    </svg>
                    Detener grabación
                  </button>
                ) : voiceEnabled && micPermission === 'unknown' ? (
                  <button
                    onClick={requestMicrophonePermission}
                    className="flex items-center px-3 py-2 bg-purple-500 text-white rounded-md hover:bg-purple-600 shadow-sm"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path d="M10 2a6 6 0 00-6 6v3.586l-.707.707A1 1 0 004 14h12a1 1 0 00.707-1.707L16 11.586V8a6 6 0 00-6-6zM10 18a3 3 0 01-3-3h6a3 3 0 01-3 3z" />
                    </svg>
                    Permitir micrófono
                  </button>
                ) : voiceEnabled && micPermission === 'denied' ? (
                  <button
                    onClick={requestMicrophonePermission}
                    className="flex items-center px-3 py-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200 shadow-sm border border-red-300"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M13.477 14.89A6 6 0 015.11 6.524l8.367 8.368zm1.414-1.414L6.524 5.11a6 6 0 018.367 8.367zM18 10a8 8 0 11-16 0 8 8 0 0116 0z" clipRule="evenodd" />
                    </svg>
                    Micrófono bloqueado
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      setVoiceEnabled(true);
                      requestMicrophonePermission();
                    }}
                    className="flex items-center px-3 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 shadow-sm"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M7 4a3 3 0 016 0v4a3 3 0 11-6 0V4zm4 10.93A7.001 7.001 0 0017 8a1 1 0 10-2 0A5 5 0 015 8a1 1 0 00-2 0 7.001 7.001 0 006 6.93V17H6a1 1 0 100 2h8a1 1 0 100-2h-3v-2.07z" clipRule="evenodd" />
                    </svg>
                    Activar modo voz
                  </button>
                )}

                {isListening && (
                  <span className="ml-2 text-green-600 flex items-center animate-pulse">
                    <span className="w-2 h-2 bg-green-600 rounded-full mr-1 animate-ping"></span>
                    Escuchando...
                  </span>
                )}
              </div>
            </div>
          </div>

          {/* Botones de navegación */}
          <div className="flex justify-between">
            <button
              onClick={handlePreviousQuestion}
              disabled={currentQuestionIndex === 0}
              className={`px-4 py-2 rounded ${
                currentQuestionIndex === 0
                  ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
            >
              Anterior
            </button>

            <button
              onClick={handleNextQuestion}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              {currentQuestionIndex < survey.questions.length - 1 ? 'Siguiente' : 'Finalizar'}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TakeSurvey;
